import React, { useState } from "react";
import axios from "axios";
import { Card, Button, Form, InputGroup, Alert, Spinner } from "react-bootstrap";
import { Eye, EyeSlash } from "react-bootstrap-icons";

const API_BASE_URL = "http://localhost:5000/api/auth";

export default function LoginPage({ onLogin }) {
  const [step, setStep] = useState("login"); // 'login' | 'register'
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);

  const resetForm = () => {
    setUsername("");
    setEmail("");
    setPassword("");
    setRole("user");
    setError(null);
    setSuccess(null);
  };

  const validateForm = () => {
    if (!username.trim()) {
      setError("Username is required");
      return false;
    }
    if (!password.trim()) {
      setError("Password is required");
      return false;
    }
    if (step === "register") {
      if (!email.trim()) {
        setError("Email is required");
        return false;
      }
      if (password.length < 6) {
        setError("Password must be at least 6 characters long");
        return false;
      }
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError("Please enter a valid email address");
        return false;
      }
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE_URL}/login`, {
        username: username.trim(),
        password,
      });
      
      // Store token in localStorage
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      
      // Call onLogin callback
      onLogin(res.data);
      
    } catch (err) {
      console.error('Login error:', err);
      setError(err.response?.data?.message || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE_URL}/register`, {
        username: username.trim(),
        email: email.trim(),
        password,
        role,
      });
      
      setSuccess("Registration successful! You can now log in.");
      resetForm();
      setStep("login");
      
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || "Registration failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const switchStep = () => {
    resetForm();
    setStep(step === "login" ? "register" : "login");
  };

  return (
    <div
      style={{
        backgroundColor: "#0b1e39",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        padding: "1rem",
      }}
    >
      <Card
        style={{
          maxWidth: "420px",
          width: "100%",
          backgroundColor: "#12345b",
          borderRadius: "16px",
          padding: "2rem",
          color: "white",
          boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
          textAlign: "center",
        }}
      >
        <h3 className="mb-4" style={{ color: "#ffffff", fontWeight: "bold" }}>
          {step === "login" ? "Login" : "Register"}
        </h3>

        {error && (
          <Alert variant="danger" className="mb-3">
            {error}
          </Alert>
        )}

        {success && (
          <Alert variant="success" className="mb-3">
            {success}
          </Alert>
        )}

        <Form onSubmit={step === "login" ? handleLogin : handleRegister}>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              disabled={loading}
              style={{
                backgroundColor: "#102c4a",
                borderColor: "#345b96",
                color: "white",
                borderRadius: "8px",
              }}
            />
          </Form.Group>

          {step === "register" && (
            <Form.Group className="mb-3">
              <Form.Control
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                disabled={loading}
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                  borderRadius: "8px",
                }}
              />
            </Form.Group>
          )}

          <Form.Group className="mb-3">
            <InputGroup>
              <Form.Control
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={loading}
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                  borderTopLeftRadius: "8px",
                  borderBottomLeftRadius: "8px",
                }}
              />
              <InputGroup.Text
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  cursor: "pointer",
                  borderTopRightRadius: "8px",
                  borderBottomRightRadius: "8px",
                }}
                onClick={() => setShowPassword((v) => !v)}
              >
                {showPassword ? <EyeSlash color="lightgray" /> : <Eye color="lightgray" />}
              </InputGroup.Text>
            </InputGroup>
          </Form.Group>

          {step === "register" && (
            <Form.Group className="mb-3">
              <Form.Select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                disabled={loading}
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                  borderRadius: "8px",
                }}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
                <option value="technician">Technician</option>
              </Form.Select>
            </Form.Group>
          )}

          <Button
            type="submit"
            variant="primary"
            disabled={loading}
            style={{ 
              borderRadius: "8px", 
              width: "100%",
              height: "45px",
              fontSize: "16px",
              fontWeight: "bold"
            }}
          >
            {loading ? (
              <>
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                  className="me-2"
                />
                {step === "login" ? "Logging in..." : "Registering..."}
              </>
            ) : (
              step === "login" ? "Login" : "Register"
            )}
          </Button>
        </Form>

        <Button
          variant="link"
          className="mt-3"
          onClick={switchStep}
          disabled={loading}
          style={{ 
            textDecoration: "underline", 
            fontSize: "0.9rem",
            color: "#87CEEB"
          }}
        >
          {step === "login" 
            ? "Don't have an account? Register" 
            : "Already have an account? Login"
          }
        </Button>
      </Card>
    </div>
  );
}
