import React, { useState } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api/auth";

export default function LoginPage({ onLogin }) {
  const [step, setStep] = useState("login"); // 'login' | 'register'
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);

  const resetForm = () => {
    setUsername("");
    setEmail("");
    setPassword("");
    setRole("user");
    setError(null);
    setSuccess(null);
  };

  const validateForm = () => {
    if (!username.trim()) {
      setError("Username is required");
      return false;
    }
    if (!password.trim()) {
      setError("Password is required");
      return false;
    }
    if (step === "register") {
      if (!email.trim()) {
        setError("Email is required");
        return false;
      }
      if (password.length < 6) {
        setError("Password must be at least 6 characters long");
        return false;
      }
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError("Please enter a valid email address");
        return false;
      }
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      console.log('Attempting login with:', { username: username.trim() });
      
      const response = await axios.post(`${API_BASE_URL}/login`, {
        username: username.trim(),
        password,
      });
      
      console.log('Login response:', response.data);
      
      // Ensure all required user data is present
      if (!response.data.user || !response.data.user.role) {
        throw new Error("Incomplete user data received from server");
      }
      
      // Store token and complete user data in localStorage
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify({
        id: response.data.user.id,
        username: response.data.user.username,
        email: response.data.user.email,
        role: response.data.user.role,
        created_at: response.data.user.created_at
      }));
      
      // Set default axios authorization header
      axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.token}`;
      
      setSuccess("Login successful!");
      
      // Call onLogin callback if provided
      if (onLogin) {
        onLogin({
          token: response.data.token,
          user: {
            id: response.data.user.id,
            username: response.data.user.username,
            email: response.data.user.email,
            role: response.data.user.role,
            created_at: response.data.user.created_at
          }
        });
      }
      
    } catch (err) {
      console.error('Login error:', err);
      console.error('Error response:', err.response?.data);
      
      if (err.response?.status === 401) {
        setError("Invalid username or password");
      } else {
        setError(
          err.response?.data?.message || 
          err.message || 
          "Login failed. Please try again."
        );
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      console.log('Attempting registration with:', { 
        username: username.trim(), 
        email: email.trim(), 
        role 
      });
      
      const response = await axios.post(`${API_BASE_URL}/register`, {
        username: username.trim(),
        email: email.trim(),
        password,
        role,
      });
      
      console.log('Registration response:', response.data);
      
      setSuccess(response.data.message || "Registration successful! You can now log in.");
      resetForm();
      setStep("login");
      
    } catch (err) {
      console.error('Registration error:', err);
      console.error('Error response:', err.response?.data);
      
      if (err.response?.status === 409) {
        setError("Username or email already exists");
      } else {
        setError(
          err.response?.data?.message || 
          err.message || 
          "Registration failed. Please try again."
        );
      }
    } finally {
      setLoading(false);
    }
  };

  const switchStep = () => {
    resetForm();
    setStep(step === "login" ? "register" : "login");
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4" style={{ backgroundColor: "#0b1e39" }}>
      <div 
        className="w-full max-w-md p-8 rounded-2xl shadow-2xl"
        style={{ backgroundColor: "#12345b" }}
      >
        <h3 className="text-2xl font-bold text-white text-center mb-6">
          {step === "login" ? "Login" : "Register"}
        </h3>

        {error && (
          <div className="mb-4 p-3 bg-red-600 text-white rounded-lg text-sm">
            {error}
            <button 
              onClick={() => setError(null)}
              className="float-right text-white hover:text-gray-300"
            >
              √ó
            </button>
          </div>
        )}

        {success && (
          <div className="mb-4 p-3 bg-green-600 text-white rounded-lg text-sm">
            {success}
            <button 
              onClick={() => setSuccess(null)}
              className="float-right text-white hover:text-gray-300"
            >
              √ó
            </button>
          </div>
        )}

        <form onSubmit={step === "login" ? handleLogin : handleRegister} className="space-y-4">
          <div>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              disabled={loading}
              className="w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
              style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
            />
          </div>

          {step === "register" && (
            <div>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                disabled={loading}
                className="w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
                style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
              />
            </div>
          )}

          <div className="relative">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              minLength={step === "register" ? 6 : undefined}
              className="w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 pr-12"
              style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
            />
            <button
              type="button"
              onClick={() => setShowPassword(!showPassword)}
              className="absolute right-3 top-3 text-gray-400 hover:text-white"
            >
              {showPassword ? "üëÅÔ∏è" : "üôà"}
            </button>
            {step === "register" && (
              <p className="text-xs text-gray-400 mt-1">
                Password must be at least 6 characters long
              </p>
            )}
          </div>

          {step === "register" && (
            <div>
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                disabled={loading}
                className="w-full p-3 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
                <option value="technician">Technician</option>
              </select>
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="w-full p-3 rounded-lg text-white font-bold text-lg hover:opacity-90 transition-opacity disabled:opacity-50"
            style={{ backgroundColor: "#345b96" }}
          >
            {loading ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                {step === "login" ? "Logging in..." : "Registering..."}
              </span>
            ) : (
              step === "login" ? "Login" : "Register"
            )}
          </button>
        </form>

        <button
          onClick={switchStep}
          disabled={loading}
          className="w-full mt-4 text-blue-300 hover:text-blue-200 transition-colors disabled:opacity-50"
        >
          {step === "login" 
            ? "Don't have an account? Register" 
            : "Already have an account? Login"
          }
        </button>
      </div>
    </div>
  );
}
