import React, { useEffect, useRef, useState } from "react";
import { Table, Button, Container, Row, Col, Card, Tabs, Tab } from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";
import NewFaultModal from "./NewFaultModal";

const assignablePersons = [
  "John Doe", "Jane Smith", "Alex Johnson", "Emily Davis"
];

function useMultiFaults() {
  const [open, setOpen] = useState([]);
  const [resolved, setResolved] = useState([]);
  const [err, setErr] = useState("");
  const token = localStorage.getItem("token");

  // fetch open/resolved faults
  const fetchOpen = async () => {
    if (!token) return setErr("No authentication token.");
    try {
      const r = await fetch("http://localhost:5000/api/faults?status=open", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOpen(await r.json());
    } catch (e) { setErr(String(e)); }
  };
  const fetchResolved = async () => {
    if (!token) return setErr("No authentication token.");
    try {
      const r = await fetch("http://localhost:5000/api/faults?status=closed", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setResolved(await r.json());
    } catch (e) { setErr(String(e)); }
  };

  // Add new: always to Open
  const create = async (data) => {
    if (!token) throw new Error("Authentication required.");
    const resp = await fetch("http://localhost:5000/api/faults", {
      method: "POST", headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify(data),
    });
    const result = await resp.json();
    setOpen(f => [...f, result.fault]);
    return true;
  };

  // Edit for Open Faults
  const update = async (data) => {
    if (!token) throw new Error("Authentication required.");
    const resp = await fetch(`http://localhost:5000/api/faults/${data.id}`, {
      method: "PUT", headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify(data),
    });
    const result = await resp.json();
    setOpen(faults => faults.map(f => f.id === result.fault.id ? result.fault : f));
    return true;
  };

  // Delete(open/resolved): Try both arrays for simplicity
  const remove = async (id) => {
    if (!token) return;
    await fetch(`http://localhost:5000/api/faults/${id}`, {
      method: "DELETE", headers: { Authorization: `Bearer ${token}` }
    });
    setOpen(faults => faults.filter(f => f.id !== id));
    setResolved(faults => faults.filter(f => f.id !== id));
  };

  // Mark as resolved: move from open to resolved arrays (instant UX)
  const resolve = async (id) => {
    if (!token) return;
    const r = await fetch(`http://localhost:5000/api/faults/${id}`, {
      method: "PUT", headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify({ Status: "closed" }),
    });
    if (!r.ok) return setErr("Failed to mark as resolved");
    const data = await r.json();
    setOpen(open => open.filter(f => f.id !== id));
    setResolved(resolved => [...resolved, data.fault]); // add to resolved
  };

  // Initial load
  useEffect(() => {
    fetchOpen();
    fetchResolved();
  }, []);

  return {
    open, resolved, fetchOpen, fetchResolved, create, update, remove, resolve, err, setErr
  };
}

// Pagination
function usePagination(list, perPage = 10) {
  const [page, setPage] = useState(1);
  const max = Math.ceil(list.length / perPage);
  const current = list.slice((page - 1) * perPage, page * perPage);
  // Reset on switch
  useEffect(() => { setPage(1); }, [list]);
  return { current, page, setPage, max };
}


export default function Dashboard({ userInfo, notifications, setNotifications, onLogout }) {
  const [modal, setModal] = useState(false);
  const [edit, setEdit] = useState(null);

  const [search, setSearch] = useState("");
  const [view, setView] = useState("faults"); // 'faults' | 'resolved'
  const [showNotif, setShowNotif] = useState(false);
  const notifRef = useRef();
  const [footerInfo, setFooterInfo] = useState(false);

  const {
    open, resolved, create, update, remove, resolve, err, setErr
  } = useMultiFaults();

  // Notifications UX
  useEffect(() => {
    if (showNotif) setNotifications(n => n.map(e => ({ ...e, isRead: true })));
    const outside = e => notifRef.current && !notifRef.current.contains(e.target) && setShowNotif(false);
    document.addEventListener("mousedown", outside);
    return () => document.removeEventListener("mousedown", outside);
  }, [showNotif, setNotifications]);

  // Search filter
  const currentFaultArr = view === "faults" ? open : resolved;
  const filtered = currentFaultArr.filter(f => {
    const str = [f.DescFault, f.Location, f.ReportedBy].join(" ").toLowerCase();
    return str.includes(search.toLowerCase());
  });

  const { current, page, setPage, max } = usePagination(filtered);

  // --- Render ---
  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-dark fixed-top shadow-sm" style={{ height: 60, backgroundColor: "#001f3f" }}>
        <Container fluid className="d-flex justify-content-between align-items-center">
          <div style={{ width: 120 }} />
          <span className="navbar-brand mb-0 h1 mx-auto">⚡ N F M System Version 1.0.1</span>
          <div className="d-flex align-items-center gap-3 position-relative">
            <div ref={notifRef} style={{ position: "relative" }}>
              <Button variant="link" className="text-white p-0"
                onClick={() => setShowNotif(v => !v)} style={{ fontSize: "1.3rem" }}>
                <BellFill />
                {notifications.some(n => !n.isRead) &&
                  <span className="position-absolute top-0 end-0 bg-danger text-white rounded-circle px-2 py-0"
                    style={{ fontSize: "0.7rem", lineHeight: 1, fontWeight: "bold" }}>
                    {notifications.filter(n => !n.isRead).length}
                  </span>}
              </Button>
              {showNotif && (
                <div className="position-absolute" style={{
                  top: 35, right: 0, background: "#fff", color: "#222", width: 280, maxHeight: 300, overflowY: "auto",
                  boxShadow: "0 4px 12px rgba(0,0,0,0.15)", borderRadius: 8, zIndex: 1500
                }}>
                  {notifications.length === 0
                    ? <div style={{ padding: 10 }}>No notifications</div>
                    : notifications.map(note => (
                      <div key={note.id}
                        style={{
                          padding: 10, borderBottom: "1px solid #eee",
                          background: note.isRead ? "#f8f9fa" : "#fff", fontWeight: note.isRead ? "normal" : 600
                        }}>
                        {note.message}
                      </div>
                    ))}
                </div>
              )}
            </div>
            <Button className="glass-button" size="sm" onClick={onLogout}>Logout</Button>
            <UserProfileDisplay user={userInfo} />
          </div>
        </Container>
      </nav>

      <Container fluid className="pt-5 mt-4">
        {err && <div className="alert alert-danger" role="alert">{err}
          <button type="button" className="btn-close float-end" onClick={() => setErr("")} aria-label="Close" />
        </div>}
        <Row>
          {/* Sidebar */}
          <Col xs={2} className="bg-dark text-white sidebar p-3 position-fixed vh-100"
            style={{ top: 60, left: 0, zIndex: 1040 }}>
            <div className="glass-sidebar-title mb-4 text-center">
              <span className="sidebar-title-text">Dashboard</span>
            </div>
            <ul className="nav flex-column">
              <li className="nav-item mb-2">
                <button className="nav-link btn btn-link text-white p-0" onClick={() => setModal(true)}>+ Add Fault</button>
              </li>
              <li className="nav-item mb-2">
                <button className={`nav-link btn btn-link text-white p-0${view==="faults"?" fw-bold":""}`}
                  onClick={() => setView("faults")}>📋 Fault Review Panel</button>
                <button className={`nav-link btn btn-link text-white p-0${view==="resolved"?" fw-bold":""}`}
                  onClick={() => setView("resolved")}>✅ Resolved Faults</button>
              </li>
            </ul>
          </Col>
          {/* Main Table */}
          <Col className="ms-auto d-flex flex-column"
            style={{
              marginLeft: "16.666667%", width: "calc(100% - 16.666667%)",
              height: "calc(100vh - 60px)", overflow: "hidden", paddingLeft: 0, maxWidth: "82%"
            }}>
            {/* TABS */}
            <Tabs activeKey={view} className="custom-tabs" justify>
              <Tab eventKey="faults" title={<span className="tab-title-lg">🚧 Faults Review Panel</span>}>
                {view === "faults" && (
                  <>
                    <Row className="mb-3 px-3">
                      <Col md={4} className="mb-2">
                        <input type="text" className="form-control" placeholder="Search faults..."
                          value={search} onChange={e => setSearch(e.target.value)} />
                      </Col>
                    </Row>
                    <div className="mb-2 px-3"><strong>Total Open Faults:</strong> {filtered.length}</div>
                    <FaultsTable
                      faults={current}
                      onEdit={setEdit}
                      onDelete={remove}
                      onMarkResolved={resolve}
                      isResolved={false}
                      page={page}
                      setPage={setPage}
                      max={max}
                    />
                  </>
                )}
              </Tab>
              <Tab eventKey="resolved" title={<span className="tab-title-lg">✅ Resolved Faults</span>}>
                {view === "resolved" && (
                  <>
                    <Row className="mb-3 px-3">
                      <Col md={4} className="mb-2">
                        <input type="text" className="form-control" placeholder="Search..."
                          value={search} onChange={e => setSearch(e.target.value)} />
                      </Col>
                    </Row>
                    <div className="mb-2 px-3"><strong>Total Resolved Faults:</strong> {filtered.length}</div>
                    <FaultsTable
                      faults={current}
                      onDelete={remove}
                      isResolved={true}
                      page={page}
                      setPage={setPage}
                      max={max}
                    />
                  </>
                )}
              </Tab>
            </Tabs>
          </Col>
        </Row>
      </Container>

      {/* Footer */}
      <footer className="fixed-bottom text-white py-2 px-3 d-flex flex-column flex-sm-row justify-content-between align-items-center shadow"
        style={{ backgroundColor: "#001f3f" }}>
        <div className="mb-2 mb-sm-0">
          <Button className="glass-button" size="sm"
            onClick={() => alert("Contact support at support@nfm.lk")}>Support</Button>
        </div>
        <div className="text-center flex-grow-1 mb-2 mb-sm-0">
          Total Open: {open.length} | Resolved: {resolved.length} | Unread Notifications: {notifications.filter(n => !n.isRead).length}
        </div>
        <div className="text-center text-sm-end">
          <Button className="glass-button" size="sm"
            onClick={() => setFooterInfo((v) => !v)}>
            {footerInfo ? "Hide Info" : "Show Info"}
          </Button>
          {footerInfo && (
            <div className="mt-1" style={{ fontSize: "0.75rem", opacity: 0.8 }}>
              © 2025 Network Fault Management System. All rights reserved.
            </div>
          )}
        </div>
      </footer>

      {/* New/Edit Modal */}
      <NewFaultModal
        show={modal || Boolean(edit)}
        handleClose={() => { setModal(false); setEdit(null); }}
        handleAdd={edit ? update : create}
        assignablePersons={assignablePersons}
        initialData={edit}
      />

      {/* Styles (same as before) */}
      <style>{/* (your style block unchanged — ommitted here for brevity) */}</style>
    </>
  );
}

// FaultsTable as subcomponent for both Open/Resolved tabs:
function FaultsTable({ faults, onEdit, onDelete, onMarkResolved, isResolved, page, setPage, max }) {
  return (
    <Row style={{ height: "calc(100vh - 60px - 130px - 80px)", overflowY: "auto" }}>
      <Card className="shadow-sm w-100"><Card.Body className="p-0 d-flex flex-column">
        <Table striped bordered hover responsive className="table-fixed-header mb-0 custom-align-table">
          <colgroup>
            <col width="3.5%" /><col width="6%" /><col width="6%" /><col width="10%" /><col width="10%" /><col width="18%" /><col width="7%" /><col width="10%" /><col width="12%" /><col width="7.5%" />
          </colgroup>
          <thead className="sticky-top bg-light">
            <tr>
              <th className="text-center">ID</th>
              <th className="text-center">System ID</th>
              <th className="text-center">Section ID</th>
              <th>Reported By</th>
              <th>Location</th>
              <th>Description</th>
              <th className="text-center">Status</th>
              <th>Assigned To</th>
              <th>Reported At</th>
              <th className="text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {faults.map(f =>
              <tr key={f.id} className="table-row-hover">
                <td className="text-center">{f.id}</td>
                <td className="text-center">{f.SystemID}</td>
                <td className="text-center">{f.SectionID}</td>
                <td>{f.ReportedBy}</td>
                <td>{f.Location}</td>
                <td className="description-col">{f.DescFault}</td>
                <td className="text-center">{f.Status}</td>
                <td>{f.AssignTo}</td>
                <td style={{ whiteSpace: "nowrap" }}>{f.DateTime ? new Date(f.DateTime).toLocaleString() : ""}</td>
                <td className="text-center">
                  {onEdit && !isResolved && <Button variant="outline-primary" size="sm" className="me-1 mb-1"
                    onClick={() => onEdit(f)}>Edit</Button>}
                  {onMarkResolved && !isResolved && <Button variant="outline-success" size="sm" className="me-1 mb-1"
                    onClick={() => onMarkResolved(f.id)}>Mark as Resolved</Button>}
                  <Button variant="outline-danger" size="sm"
                    onClick={() => onDelete(f.id)}>Delete</Button>
                </td>
              </tr>
            )}
            {faults.length === 0 && <tr><td colSpan={10} className="text-center text-muted py-4">No faults.</td></tr>}
          </tbody>
        </Table>
        <nav aria-label="Fault pagination" className="mt-3 px-3" style={{ flexShrink: 0 }}>
          <ul className="pagination justify-content-center mb-0">
            <li className={`page-item ${page === 1 ? "disabled" : ""}`}>
              <button className="page-link" onClick={() => setPage(Math.max(page - 1, 1))}>Previous</button>
            </li>
            {Array.from({ length: max }, (_, i) => (
              <li key={i + 1} className={`page-item ${page === i + 1 ? "active" : ""}`}>
                <button className="page-link" onClick={() => setPage(i + 1)}>{i + 1}</button>
              </li>
            ))}
            <li className={`page-item ${page === max || !max ? "disabled" : ""}`}>
              <button className="page-link" onClick={() => setPage(Math.min(page + 1, max))}>Next</button>
            </li>
          </ul>
        </nav>
      </Card.Body></Card>
    </Row>
  );
}
