import React, { useEffect, useState, useRef } from "react";
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Card,
  Tabs,
  Tab,
} from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";
import NewFaultModal from "./NewFaultModal";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Dashboard({
  userInfo,
  notifications,
  setNotifications,
  onLogout,
}) {
  const navigate = useNavigate();
  const [showFooterInfo, setShowFooterInfo] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const notifRef = useRef();
  const [showNewFaultModal, setShowNewFaultModal] = useState(false);
  const [editFault, setEditFault] = useState(null);
  const [faults, setFaults] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  
  const assignablePersons = ["John Doe", "Jane Smith", "Alex Johnson", "Emily Davis"];

  // Fetch faults on component mount
  useEffect(() => {
    fetchFaults();
  }, []);

  const fetchFaults = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get('/api/faults');
      setFaults(response.data.faults);
    } catch (error) {
      console.error('Error fetching faults:', error);
      alert('Failed to fetch faults');
    } finally {
      setIsLoading(false);
    }
  };

  // Rest of your existing code (notification handlers, logout, etc.)...

  const handleNewFault = async (faultData) => {
    try {
      const response = await axios.post('/api/faults', {
        systemID: faultData.systemID,
        sectionID: faultData.sectionID,
        reportedBy: faultData.reportedBy,
        location: faultData.location,
        description: faultData.description,
        urgency: faultData.urgency,
        assignedTo: faultData.assignedTo
      });
      
      setFaults([response.data.fault, ...faults]);
      return true;
    } catch (error) {
      console.error('Error creating fault:', error);
      alert('Failed to create fault');
      return false;
    }
  };

  // Rest of your CRUD operations...

  const [searchTerm, setSearchTerm] = useState("");
  const [filterUrgency, setFilterUrgency] = useState("all");
  const [filterStatus, setFilterStatus] = useState("all");

  const filteredFaults = faults.filter((fault) => {
    const matchesSearch =
      (fault.DescFault && fault.DescFault.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (fault.Location && fault.Location.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (fault.ReportedBy && fault.ReportedBy.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesUrgency = filterUrgency === "all" || (fault.Urgency && fault.Urgency.toLowerCase() === filterUrgency);
    const matchesStatus = filterStatus === "all" || (fault.Status && fault.Status.toLowerCase() === filterStatus.toLowerCase());

    return matchesSearch && matchesUrgency && matchesStatus;
  });

  return (
    <>
      {/* Navbar - Keep exactly as before */}
      <nav className="navbar navbar-dark fixed-top shadow-sm" style={{ height: 60, backgroundColor: "#001f3f" }}>
        {/* ... existing navbar code ... */}
      </nav>

      {/* Main Content */}
      <Container fluid className="pt-5 mt-4">
        <Row className="mb-3 align-items-center">
          <Col>
            <Tabs defaultActiveKey="faults" id="fault-tabs" className="custom-tabs" justify>
              <Tab eventKey="faults" title={<span className="tab-title-lg">ðŸš§ Faults Review Panel</span>}>
                <div className="text-end mt-3 mb-2">
                  <Button variant="primary" size="sm" onClick={() => setShowNewFaultModal(true)}>
                    + New Fault
                  </Button>
                </div>
                
                {/* Search and Filter Row - Keep styling */}
                <Row className="mb-3 px-3">
                  <Col md={4} className="mb-2">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Search faults..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                  </Col>
                  <Col md={3} className="mb-2">
                    <select
                      className="form-select"
                      value={filterUrgency}
                      onChange={(e) => setFilterUrgency(e.target.value)}
                    >
                      <option value="all">All Urgencies</option>
                      <option value="high">High</option>
                      <option value="medium">Medium</option>
                      <option value="low">Low</option>
                    </select>
                  </Col>
                  <Col md={3} className="mb-2">
                    <select
                      className="form-select"
                      value={filterStatus}
                      onChange={(e) => setFilterStatus(e.target.value)}
                    >
                      <option value="all">All Statuses</option>
                      <option value="open">Open</option>
                      <option value="closed">Closed</option>
                    </select>
                  </Col>
                </Row>

                {/* Faults Table - Maintain all styling */}
                <Card className="shadow-sm">
                  <Card.Body className="p-0">
                    <Table striped bordered hover responsive className="table-fixed-header table-lg mb-0">
                      <thead className="sticky-top bg-light">
                        <tr>
                          <th>ID</th>
                          <th>System</th>
                          <th>Section</th>
                          <th>Reported By</th>
                          <th>Location</th>
                          <th>Description</th>
                          <th>Urgency</th>
                          <th>Status</th>
                          <th>Assigned To</th>
                          <th>Reported At</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {isLoading ? (
                          <tr>
                            <td colSpan="11" className="text-center">Loading faults...</td>
                          </tr>
                        ) : filteredFaults.length === 0 ? (
                          <tr>
                            <td colSpan="11" className="text-center">
                              <div className="py-4" style={{ fontSize: "1.1rem", color: "#6c757d" }}>
                                <i className="bi bi-search me-2"></i>
                                No faults found matching your criteria
                              </div>
                            </td>
                          </tr>
                        ) : (
                          filteredFaults.map((fault) => (
                            <tr key={fault.id} className="table-row-hover">
                              <td>{fault.id}</td>
                              <td>{fault.System}</td>
                              <td>{fault.section_name}</td>
                              <td>{fault.ReportedBy}</td>
                              <td>{fault.Location}</td>
                              <td className="description-col">{fault.DescFault}</td>
                              <td><span className={`badge bg-${getUrgencyColor(fault.Urgency)}`}>{fault.Urgency}</span></td>
                              <td>{fault.Status}</td>
                              <td>{fault.AssignTo}</td>
                              <td>{new Date(fault.DateTime).toLocaleString()}</td>
                              <td>
                                <Button variant="outline-success" size="sm" className="me-1 mb-1" onClick={() => toggleStatus(fault.id)}>
                                  {fault.Status === "Open" ? "Mark Closed" : "Reopen"}
                                </Button>
                                <Button variant="outline-primary" size="sm" className="me-1 mb-1" onClick={() => setEditFault({
                                  id: fault.id,
                                  systemID: fault.SystemID,
                                  sectionID: fault.SectionID,
                                  reportedBy: fault.ReportedBy,
                                  location: fault.Location,
                                  description: fault.DescFault,
                                  urgency: fault.Urgency,
                                  status: fault.Status,
                                  assignedTo: fault.AssignTo
                                })}>Edit</Button>
                                <Button variant="outline-danger" size="sm" onClick={() => handleDeleteFault(fault.id)}>Delete</Button>
                              </td>
                            </tr>
                          ))
                        )}
                      </tbody>
                    </Table>
                  </Card.Body>
                </Card>
              </Tab>
            </Tabs>
          </Col>
        </Row>
      </Container>

      {/* Footer - Keep exactly as before */}
      <footer className="fixed-bottom text-white py-2 px-3 d-flex flex-column flex-sm-row justify-content-between align-items-center shadow" style={{ backgroundColor: "#001f3f" }}>
        {/* ... existing footer code ... */}
      </footer>

      {/* Modal */}
      <NewFaultModal
        show={showNewFaultModal || !!editFault}
        handleClose={() => {
          setShowNewFaultModal(false);
          setEditFault(null);
        }}
        handleAdd={async (data) => {
          if (editFault) {
            const success = await handleUpdateFault({ ...editFault, ...data });
            if (success) {
              setEditFault(null);
              setShowNewFaultModal(false);
            }
          } else {
            const success = await handleNewFault(data);
            if (success) {
              setShowNewFaultModal(false);
            }
          }
        }}
        assignablePersons={assignablePersons}
        initialData={editFault}
      />

      {/* Keep all existing styles */}
      <style>{`
        .glass-button {
          background: rgba(255, 255, 255, 0.1);
          border: 1.5px solid rgba(255, 255, 255, 0.4);
          border-radius: 12px;
          backdrop-filter: blur(10px);
          color: white;
          font-weight: 600;
          padding: 0.4rem 0.9rem;
          transition: all 0.3s ease-in-out;
          cursor: pointer;
        }
        .glass-button:hover {
          background: rgba(255, 255, 255, 0.35);
          color: #001f3f;
          transform: scale(1.07);
          box-shadow: 0 0 8px rgba(255, 255, 255, 0.6);
        }
        .custom-tabs .nav-link {
          font-weight: 600;
          color: #001f3f;
          border-radius: 10px;
          transition: all 0.3s ease;
        }
        .custom-tabs .nav-link.active {
          background: linear-gradient(to right, #00c6ff, #0072ff);
          color: white !important;
          box-shadow: 0 0 8px rgba(0, 114, 255, 0.5);
        }
        .table-fixed-header thead.sticky-top th {
          position: sticky;
          top: 0;
          z-index: 10;
          background-color: #f8f9fa;
          border-bottom: 2px solid #dee2e6;
        }
        .table-lg td, .table-lg th {
          font-size: 1.15rem;
          padding: 1rem 1.25rem;
          vertical-align: middle;
        }
        .tab-title-lg {
          font-size: 1.6rem;
          font-weight: 700;
          color: #001f3f;
          letter-spacing: 0.5px;
          text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
        }
        .description-col {
          max-width: 300px;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }
        .table-row-hover:hover {
          background-color: #e6f0ff;
          cursor: pointer;
          transition: background-color 0.25s ease;
        }
        .form-control, .form-select {
          font-size: 1rem;
          border-radius: 8px;
        }
      `}</style>
    </>
  );
}

// Helper
function getUrgencyColor(level) {
  if (!level) return "light";
  switch (level.toLowerCase()) {
    case "high": return "danger";
    case "medium": return "warning";
    case "low": return "secondary";
    default: return "light";
  }
}
