import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const NewFaultModal = ({ show, handleClose, handleAdd, assignablePersons, initialData }) => {
  const [formData, setFormData] = useState({
    systemID: "",
    location: "",
    description: "",
    reportedBy: "",
    assignedTo: "",
    status: "Open",
    sectionID: ""
  });

  useEffect(() => {
    if (initialData) {
      setFormData({
        systemID: initialData.systemID || "",
        location: initialData.location || "",
        description: initialData.description || "",
        reportedBy: initialData.reportedBy || "",
        assignedTo: initialData.assignedTo || "",
        status: initialData.status || "Open",
        sectionID: initialData.sectionID || ""
      });
    } else {
      setFormData({
        systemID: "",
        location: "",
        description: "",
        reportedBy: "",
        assignedTo: "",
        status: "Open",
        sectionID: ""
      });
    }
  }, [initialData]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const success = await handleAdd(formData);
    if (success) handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{initialData ? "Edit Fault" : "New Fault"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>System ID</Form.Label>
            <Form.Control
              type="number"
              value={formData.systemID}
              onChange={(e) => setFormData({ ...formData, systemID: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Location</Form.Label>
            <Form.Control
              type="text"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Reported By</Form.Label>
            <Form.Control
              type="text"
              value={formData.reportedBy}
              onChange={(e) => setFormData({ ...formData, reportedBy: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Assigned To</Form.Label>
            <Form.Control
              as="select"
              value={formData.assignedTo}
              onChange={(e) => setFormData({ ...formData, assignedTo: e.target.value })}
              required
            >
              {assignablePersons.map(person => (
                <option key={person} value={person}>{person}</option>
              ))}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Status</Form.Label>
            <Form.Control
              as="select"
              value={formData.status}
              onChange={(e) => setFormData({ ...formData, status: e.target.value })}
              required
            >
              <option value="Open">Open</option>
              <option value="Closed">Closed</option>
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Section ID</Form.Label>
            <Form.Control
              type="number"
              value={formData.sectionID}
              onChange={(e) => setFormData({ ...formData, sectionID: e.target.value })}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            {initialData ? "Update Fault" : "Add Fault"}
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default NewFaultModal;
