import React, { useEffect, useState, useRef } from "react";
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Card,
  Tabs,
  Tab,
  Modal,
  Form,
  Alert,
  Spinner,
} from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";

const assignablePersons = [
  "John Doe",
  "Jane Smith",
  "Alex Johnson",
  "Emily Davis",
];

function NewFaultModal({ show, handleClose, handleAdd, assignablePersons, initialData = null }) {
  const emptyForm = {
    SystemID: "",
    SectionID: "",
    ReportedBy: "",
    Location: "",
    DescFault: "",
    Status: "Open",
    AssignTo: assignablePersons?.[0] || "",
  };

  const [formData, setFormData] = useState(emptyForm);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [validated, setValidated] = useState(false);

  React.useEffect(() => {
    setFormData(
      initialData
        ? {
            ...emptyForm,
            ...initialData,
            SystemID: initialData.SystemID || "",
            SectionID: initialData.SectionID || "",
            ReportedBy: initialData.ReportedBy || "",
            Location: initialData.Location || "",
            DescFault: initialData.DescFault || "",
            Status: initialData.Status || "Open",
            AssignTo:
              initialData.AssignTo || assignablePersons?.[0] || "",
          }
        : emptyForm
    );
    setValidated(false);
    setError("");
    setIsSubmitting(false);
  }, [initialData, assignablePersons, show]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    error && setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.currentTarget.checkValidity() === false) return setValidated(true);
    setIsSubmitting(true);
    setError("");
    try {
      const dataToSubmit = {
        ...formData,
        id: initialData?.id,
        SystemID: parseInt(formData.SystemID, 10),
        SectionID: parseInt(formData.SectionID, 10),
      };
      const success = await handleAdd(dataToSubmit);
      if (success) {
        handleClose();
        setFormData(emptyForm);
        setValidated(false);
      }
    } catch (err) {
      setError(err.message || "Failed to submit form. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Modal show={show} onHide={() => !isSubmitting && handleClose()} centered backdrop={isSubmitting ? "static" : true}>
      <Modal.Header closeButton={!isSubmitting}>
        <Modal.Title>{initialData ? "Edit Fault Report" : "New Fault Report"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {error && (
          <Alert variant="danger" dismissible onClose={() => setError("")}>
            {error}
          </Alert>
        )}
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Group controlId="formSystemID">
                <Form.Label>
                  System ID <span className="text-danger">*</span>
                </Form.Label>
                <Form.Control
                  type="number"
                  name="SystemID"
                  value={formData.SystemID}
                  onChange={handleChange}
                  placeholder="Enter system ID"
                  required
                  min={1}
                  disabled={isSubmitting}
                />
                <Form.Control.Feedback type="invalid">Please provide a valid system ID.</Form.Control.Feedback>
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group controlId="formSectionID">
                <Form.Label>
                  Section ID <span className="text-danger">*</span>
                </Form.Label>
                <Form.Control
                  type="number"
                  name="SectionID"
                  value={formData.SectionID}
                  onChange={handleChange}
                  placeholder="Enter section ID"
                  required
                  min={1}
                  disabled={isSubmitting}
                />
                <Form.Control.Feedback type="invalid">Please provide a valid section ID.</Form.Control.Feedback>
              </Form.Group>
            </Col>
          </Row>

          <Form.Group controlId="formReportedBy" className="mb-3">
            <Form.Label>
              Reported By <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              type="text"
              name="ReportedBy"
              value={formData.ReportedBy}
              onChange={handleChange}
              placeholder="Enter reporter name"
              required
              maxLength={100}
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">Please provide the reporter's name.</Form.Control.Feedback>
          </Form.Group>

          <Form.Group controlId="formLocation" className="mb-3">
            <Form.Label>
              Location <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              type="text"
              name="Location"
              value={formData.Location}
              onChange={handleChange}
              placeholder="Enter location"
              required
              maxLength={200}
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">Please provide the location.</Form.Control.Feedback>
          </Form.Group>

          <Form.Group controlId="formDescFault" className="mb-3">
            <Form.Label>
              Description <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="DescFault"
              value={formData.DescFault}
              onChange={handleChange}
              placeholder="Describe the fault in detail"
              required
              maxLength={500}
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">Please provide a description of the fault.</Form.Control.Feedback>
            <Form.Text className="text-muted">{formData.DescFault.length}/500 characters</Form.Text>
          </Form.Group>

          <Row>
            {initialData && (
              <Col md={6} className="mb-3">
                <Form.Group controlId="formStatus">
                  <Form.Label>
                    Status <span className="text-danger">*</span>
                  </Form.Label>
                  <Form.Select name="Status" value={formData.Status} onChange={handleChange} required disabled={isSubmitting}>
                    <option value="Open">Open</option>
                    <option value="Closed">Closed</option>
                  </Form.Select>
                  <Form.Control.Feedback type="invalid">Please select a status.</Form.Control.Feedback>
                </Form.Group>
              </Col>
            )}
            <Col md={initialData ? 6 : 12} className="mb-3">
              <Form.Group controlId="formAssignTo">
                <Form.Label>
                  Assigned To <span className="text-danger">*</span>
                </Form.Label>
                <Form.Select
                  name="AssignTo"
                  value={formData.AssignTo}
                  onChange={handleChange}
                  required
                  disabled={assignablePersons.length === 0 || isSubmitting}
                >
                  {assignablePersons.length === 0 ? (
                    <option value="" disabled>
                      No persons available
                    </option>
                  ) : (
                    assignablePersons.map((person) => (
                      <option key={person} value={person}>
                        {person}
                      </option>
                    ))
                  )}
                </Form.Select>
                <Form.Control.Feedback type="invalid">Please select an assignee.</Form.Control.Feedback>
              </Form.Group>
            </Col>
          </Row>

          <Modal.Footer className="px-0">
            <Button variant="secondary" onClick={() => !isSubmitting && handleClose()} disabled={isSubmitting}>
              Cancel
            </Button>
            <Button variant="primary" type="submit" disabled={isSubmitting || assignablePersons.length === 0}>
              {isSubmitting ? (
                <>
                  <Spinner as="span" animation="border" size="sm" className="me-2" />
                  {initialData ? "Updating..." : "Creating..."}
                </>
              ) : initialData ? (
                "Update Fault"
              ) : (
                "Create Fault"
              )}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default function DashboardViewOnly({ userInfo, notifications, setNotifications, onLogout }) {
  const notifRef = useRef();
  const [showNotifications, setShowNotifications] = useState(false);
  const [showNewFaultModal, setShowNewFaultModal] = useState(false);
  const [faults, setFaults] = useState([]);
  const [resolvedFaults, setResolvedFaults] = useState([]);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [view, setView] = useState("faults"); // 'faults' or 'resolved'
  const [currentPage, setCurrentPage] = useState(1);
  const [showFooterInfo, setShowFooterInfo] = useState(false);
  const itemsPerPage = 10;

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchFaults = async () => {
      if (!token) {
        setError("No authentication token found.");
        return;
      }
      try {
        const [openRes, closedRes] = await Promise.all([
          fetch("http://localhost:5000/api/faults?status=open", { headers: { Authorization: `Bearer ${token}` } }),
          fetch("http://localhost:5000/api/faults?status=closed", { headers: { Authorization: `Bearer ${token}` } }),
        ]);
        if (!openRes.ok || !closedRes.ok) throw new Error("Failed to fetch faults");
        const openData = await openRes.json();
        const closedData = await closedRes.json();
        setFaults(openData);
        setResolvedFaults(closedData);
      } catch (e) {
        setError(e.message);
      }
    };
    fetchFaults();
  }, [token]);

  // Search & pagination data selection
  const dataToDisplay = view === "resolved" ? resolvedFaults : faults;
  const filteredFaults = dataToDisplay.filter(
    (fault) =>
      fault &&
      [fault.DescFault, fault.Location, fault.ReportedBy]
        .join(" ")
        .toLowerCase()
        .includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredFaults.length / itemsPerPage);
  const currentSlice = filteredFaults.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  useEffect(() => setCurrentPage(1), [view, searchTerm]);

  useEffect(() => {
    if (showNotifications && notifications.some((n) => !n.isRead)) {
      setNotifications((ns) => ns.map((n) => ({ ...n, isRead: true })));
    }
  }, [showNotifications, notifications, setNotifications]);

  useEffect(() => {
    const clickOutside = (e) => {
      if (notifRef.current && !notifRef.current.contains(e.target)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", clickOutside);
    return () => document.removeEventListener("mousedown", clickOutside);
  }, []);

  // Handle new fault submit
  const handleNewFaultSubmit = async (data) => {
    if (!token) {
      setError("Authentication required");
      return false;
    }
    try {
      const res = await fetch("http://localhost:5000/api/faults", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          SystemID: parseInt(data.SystemID),
          Location: data.Location,
          LocFaultID: null,
          DescFault: data.DescFault,
          ReportedBy: data.ReportedBy,
          ExtNo: null,
          AssignTo: data.AssignTo,
          Status: data.Status,
          SectionID: parseInt(data.SectionID),
          FaultForwardID: null,
        }),
      });
      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.message || "Failed to create fault");
      }
      const result = await res.json();
      setFaults((prev) => [...prev, result.fault]);
      if (result.fault.Status.toLowerCase() === "closed") {
        setResolvedFaults((prev) => [...prev, result.fault]);
      }
      return true;
    } catch (e) {
      setError(e.message);
      return false;
    }
  };

  return (
    <>
      <nav className="navbar navbar-dark fixed-top shadow-sm" style={{ height: 60, backgroundColor: "#001f3f" }}>
        <Container fluid className="d-flex justify-content-between align-items-center">
          <div style={{ width: 120 }}></div>
          <span className="navbar-brand mb-0 h1 mx-auto">⚡ N F M System Version 1.0.1</span>
          <div className="d-flex align-items-center gap-3 position-relative">
            <div ref={notifRef} style={{ position: "relative" }}>
              <Button variant="link" className="text-white p-0" onClick={() => setShowNotifications((v) => !v)} style={{ fontSize: "1.3rem" }} aria-label="Toggle Notifications">
                <BellFill />
                {notifications.some((n) => !n.isRead) && (
                  <span className="position-absolute top-0 end-0 bg-danger text-white rounded-circle px-2 py-0" style={{ fontSize: "0.7rem", lineHeight: 1, fontWeight: "bold" }}>
                    {notifications.filter((n) => !n.isRead).length}
                  </span>
                )}
              </Button>
              {showNotifications && (
                <div className="position-absolute" style={{ top: 35, right: 0, backgroundColor: "white", color: "#222", width: 280, maxHeight: 300, overflowY: "auto", boxShadow: "0 4px 12px rgba(0,0,0,0.15)", borderRadius: 8, zIndex: 1500 }}>
                  {notifications.length === 0 ? (
                    <div style={{ padding: 10 }}>No notifications</div>
                  ) : (
                    notifications.map((note) => (
                      <div key={note.id} style={{ padding: 10, borderBottom: "1px solid #eee", backgroundColor: note.isRead ? "#f8f9fa" : "white", fontWeight: note.isRead ? "normal" : 600 }}>
                        {note.message}
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>
            <Button className="glass-button" size="sm" onClick={onLogout}>
              Logout
            </Button>
            <UserProfileDisplay user={userInfo} />
          </div>
        </Container>
      </nav>

      <Container fluid className="pt-5 mt-4">
        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
            <button type="button" className="btn-close float-end" onClick={() => setError("")} aria-label="Close"></button>
          </div>
        )}
        <Row>
          <Col xs={2} className="bg-dark text-white sidebar p-3 position-fixed vh-100" style={{ top: 60, left: 0, zIndex: 1040 }}>
            <div className="glass-sidebar-title mb-4 text-center">
              <span className="sidebar-title-text">Dashboard</span>
            </div>
            <ul className="nav flex-column">
              <li className="nav-item mb-2">
                <button className="nav-link btn btn-link text-white p-0" onClick={() => setShowNewFaultModal(true)}>
                  + Add Fault
                </button>
              </li>
              <li className="nav-item mb-2">
                <button className={`nav-link btn btn-link text-white p-0 ${view === "faults" ? "fw-bold" : ""}`}
                  onClick={() => setView("faults")}>📋 Fault Review Panel</button>
                <button className={`nav-link btn btn-link text-white p-0 ${view === "resolved" ? "fw-bold" : ""}`}
                  onClick={() => setView("resolved")}>✅ Resolved Faults</button>
              </li>
            </ul>
          </Col>

          <Col className="ms-auto" style={{ marginLeft: "16.6666667%", width: "calc(100% - 16.6666667%)", height: "calc(100vh - 60px)", overflowY: "auto", paddingLeft: 0, maxWidth: "82%" }}>
            <Row className="mb-3 px-3">
              <Col md={4}>
                <input type="text" className="form-control" placeholder={"Search " + (view === "resolved" ? "Resolved" : "") + " Faults"} value={searchTerm} onChange={e => setSearchTerm(e.target.value)} aria-label="Search faults" />
              </Col>
            </Row>

            <div className="mb-2 px-3">
              <strong>Total {view === "resolved" ? "Resolved " : ""}Faults: </strong>
              {(view === "resolved" ? resolvedFaults.length : faults.length)}
            </div>

            <Row>
              <Card className="shadow-sm w-100" style={{ minWidth: 0 }}>
                <Card.Body className="p-0 d-flex flex-column">
                  <Table striped bordered hover responsive className="table-fixed-header table-fit mb-0 flex-grow-1 align-middle custom-align-table" aria-label="Faults Table">
                    <colgroup>
                      <col style={{ width: "3.5%", textAlign: "center" }} />
                      <col style={{ width: "6%" }} />
                      <col style={{ width: "6%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "18%" }} />
                      <col style={{ width: "7%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "12%" }} />
                    </colgroup>
                    <thead className="sticky-top bg-light">
                      <tr>
                        <th className="text-center">ID</th>
                        <th className="text-center">System ID</th>
                        <th className="text-center">Section ID</th>
                        <th>Reported By</th>
                        <th>Location</th>
                        <th>Description</th>
                        <th className="text-center">Status</th>
                        <th>Assigned To</th>
                        <th>Reported At</th>
                      </tr>
                    </thead>
                    <tbody>
                      {(view === "resolved" ? resolvedFaults : faults)
                        .filter(fault =>
                          fault && [fault.DescFault, fault.Location, fault.ReportedBy]
                            .join(" ")
                            .toLowerCase()
                            .includes(searchTerm.toLowerCase())
                        )
                        .map(fault => (
                          <tr key={fault.id} className="table-row-hover">
                            <td className="text-center">{fault.id}</td>
                            <td className="text-center">{fault.SystemID}</td>
                            <td className="text-center">{fault.SectionID}</td>
                            <td>{fault.ReportedBy}</td>
                            <td>{fault.Location}</td>
                            <td className="description-col">{fault.DescFault}</td>
                            <td className="text-center">{fault.Status}</td>
                            <td>{fault.AssignTo}</td>
                            <td style={{ whiteSpace: "nowrap" }}>{fault.DateTime ? new Date(fault.DateTime).toLocaleString() : ""}</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Row>
          </Col>
        </Row>
      </Container>

      <footer className="fixed-bottom text-white py-2 px-3 d-flex flex-column flex-sm-row justify-content-between align-items-center shadow" style={{ backgroundColor: "#001f3f" }}>
        <div className="mb-2 mb-sm-0">
          <Button className="glass-button" size="sm" onClick={() => alert("Contact support at support@nfm.lk")}>Support</Button>
        </div>
        <div className="text-center flex-grow-1 mb-2 mb-sm-0" aria-live="polite">
          Total Faults: {faults.length} | Unread Notifications: {notifications.filter(n => !n.isRead).length}
        </div>
        <div className="text-center text-sm-end">
          <Button className="glass-button" size="sm" onClick={() => setShowFooterInfo(v => !v)}>{showFooterInfo ? "Hide Info" : "Show Info"}</Button>
          {showFooterInfo && <div className="mt-1" style={{ fontSize: "0.75rem", opacity: 0.8 }}>© 2025 Network Fault Management System. All rights reserved.</div>}
        </div>
      </footer>

      <NewFaultModal
        show={showNewFaultModal}
        handleClose={() => setShowNewFaultModal(false)}
        handleAdd={async (data) => {
          if (!token) {
            setError("Authentication required");
            return false;
          }
          try {
            const response = await fetch("http://localhost:5000/api/faults", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
              },
              body: JSON.stringify({
                SystemID: parseInt(data.SystemID),
                Location: data.Location,
                LocFaultID: null,
                DescFault: data.DescFault,
                ReportedBy: data.ReportedBy,
                ExtNo: null,
                AssignTo: data.AssignTo,
                Status: data.Status,
                SectionID: parseInt(data.SectionID),
                FaultForwardID: null,
              }),
            });
            const result = await response.json();
            if (!response.ok) throw new Error(result.message || "Failed to create fault");
            setFaults((prev) => [...prev, result.fault]);
            if (result.fault.Status.toLowerCase() === "closed") {
              setResolvedFaults((prev) => [...prev, result.fault]);
            }
            return true;
          } catch (err) {
            setError(err.message);
            return false;
          }
        }}
        assignablePersons={assignablePersons}
      />
      {/* Put your styles here or import your CSS */}
      {/* ... */}
    </>
  );
}
