import React, { useEffect, useState, useRef } from "react";
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Card,
  Tabs,
  Tab,
} from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";
import NewFaultModal from "./NewFaultModal";

const assignablePersons = [
  "John Doe",
  "Jane Smith",
  "Alex Johnson",
  "Emily Davis",
];

export default function Dashboard({ userInfo, notifications, setNotifications, onLogout }) {
  const notifRef = useRef();
  const [showNotif, setShowNotif] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState("");
  const [view, setView] = useState("faults"); // 'faults' or 'resolved'

  const [openFaults, setOpenFaults] = useState([]);
  const [resolvedFaults, setResolvedFaults] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  const token = localStorage.getItem("token");

  // Fetch open and resolved faults separately
  const fetchOpenFaults = async () => {
    if (!token) return setError("No authentication token.");
    try {
      const res = await fetch("http://localhost:5000/api/faults?status=open", {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error("Failed to fetch open faults");
      const data = await res.json();
      setOpenFaults(data);
      setError("");
    } catch (e) {
      setError(e.message);
    }
  };

  const fetchResolvedFaults = async () => {
    if (!token) return setError("No authentication token.");
    try {
      const res = await fetch("http://localhost:5000/api/faults?status=closed", {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error("Failed to fetch resolved faults");
      const data = await res.json();
      // Filter just to be safe
      setResolvedFaults(data.filter(fault => fault.Status.toLowerCase() === "closed"));
      setError("");
    } catch (e) {
      setError(e.message);
    }
  };

  useEffect(() => {
    fetchOpenFaults();
    fetchResolvedFaults();
  }, []);

  // Mark fault as resolved (update backend and move fault locally)
  const resolveFault = async (id) => {
    if (!token) return setError("No authentication token.");
    try {
      const fault = openFaults.find(f => f.id === id);
      if (!fault) throw new Error("Fault not found");

      const updated = {
        SystemID: fault.SystemID,
        SectionID: fault.SectionID,
        ReportedBy: fault.ReportedBy,
        Location: fault.Location,
        DescFault: fault.DescFault,
        Status: "Closed",
        AssignTo: fault.AssignTo
      };

      const res = await fetch(`http://localhost:5000/api/faults/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(updated),
      });
      if (!res.ok) {
        const errData = await res.json().catch(() => ({}));
        throw new Error(errData.message || "Failed to mark as resolved");
      }
      const result = await res.json();

      // Update local states without full refetch
      setOpenFaults(o => o.filter(f => f.id !== id));
      setResolvedFaults(r => [...r, result.fault]);
    } catch (e) {
      setError(e.message);
    }
  };

  // Search & Pagination
  const dataToShow = view === "resolved" ? resolvedFaults : openFaults;
  const filteredFaults = dataToShow.filter(fault => {
    if (!fault) return false;
    const haystack = [fault.DescFault, fault.Location, fault.ReportedBy].join(" ").toLowerCase();
    return haystack.includes(searchTerm.toLowerCase());
  });

  const totalPages = Math.ceil(filteredFaults.length / itemsPerPage);
  const pageStart = (currentPage - 1) * itemsPerPage;
  const currentFaults = filteredFaults.slice(pageStart, pageStart + itemsPerPage);

  React.useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, view]);

  // Notifications: Mark read & close on outside click
  useEffect(() => {
    if (showNotif) setNotifications(n => n.map(e => ({ ...e, isRead: true })));
    const outsideClick = e => { if (notifRef.current && !notifRef.current.contains(e.target)) setShowNotif(false); };
    document.addEventListener("mousedown", outsideClick);
    return () => document.removeEventListener("mousedown", outsideClick);
  }, [showNotif, setNotifications]);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-dark fixed-top shadow-sm" style={{ height: 60, backgroundColor: "#001f3f" }}>
        <Container fluid className="d-flex justify-content-between align-items-center">
          <div style={{ width: 120 }} />
          <span className="navbar-brand mb-0 h1 mx-auto">âš¡ N F M System Version 1.0.1</span>
          <div className="d-flex align-items-center gap-3 position-relative">
            <div ref={notifRef} style={{ position: "relative" }}>
              <Button variant="link" className="text-white p-0" onClick={() => setShowNotif(v => !v)} style={{ fontSize: "1.3rem" }} aria-label="Toggle Notifications">
                <BellFill />
                {notifications.some(n => !n.isRead) && (
                  <span className="position-absolute top-0 end-0 bg-danger text-white rounded-circle px-2 py-0" style={{ fontSize: "0.7rem", lineHeight: 1, fontWeight: "bold" }}>
                    {notifications.filter(n => !n.isRead).length}
                  </span>
                )}
              </Button>
              {showNotif && (
                <div className="position-absolute" style={{ top: 35, right: 0, backgroundColor: "white", color: "#222", width: 280, maxHeight: 300, overflowY: "auto", boxShadow: "0 4px 12px rgba(0,0,0,0.15)", borderRadius: 8, zIndex: 1500 }}>
                  {notifications.length === 0 ? (
                    <div style={{ padding: 10 }}>No notifications</div>
                  ) : (
                    notifications.map(note => (
                      <div key={note.id} style={{ padding: 10, borderBottom: "1px solid #eee", backgroundColor: note.isRead ? "#f8f9fa" : "white", fontWeight: note.isRead ? "normal" : 600 }}>{note.message}</div>
                    ))
                  )}
                </div>
              )}
            </div>
            <Button className="glass-button" size="sm" onClick={onLogout}>Logout</Button>
            <UserProfileDisplay user={userInfo} />
          </div>
        </Container>
      </nav>

      {/* Main Content */}
      <Container fluid className="pt-5 mt-4">
        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
            <button type="button" onClick={() => setError("")} className="btn-close float-end" aria-label="Close" />
          </div>
        )}
        <Row>
          {/* Sidebar */}
          <Col xs={2} className="bg-dark text-white sidebar p-3 position-fixed vh-100" style={{ top: 60, left: 0, zIndex: 1040 }}>
            <div className="glass-sidebar-title mb-4 text-center"><span className="sidebar-title-text">Dashboard</span></div>
            <ul className="nav flex-column">
              <li className="nav-item mb-2">
                <button className={`nav-link btn btn-link text-white p-0 ${view === "faults" ? "fw-bold" : ""}`} onClick={() => setView("faults")}>ðŸ“‹ Fault Review Panel</button>
                <button className={`nav-link btn btn-link text-white p-0 ${view === "resolved" ? "fw-bold" : ""}`} onClick={() => setView("resolved")}>âœ… Resolved Faults</button>
              </li>
            </ul>
          </Col>

          {/* Table */}
          <Col className="ms-auto d-flex flex-column" style={{ marginLeft: "16.666667%", width: "calc(100% - 16.666667%)", height: "calc(100vh - 60px)", overflow: "auto", paddingLeft: 0, maxWidth: "82%" }}>
            <Row className="mb-3 px-3">
              <Col md={4} className="mb-2">
                <input
                  type="text"
                  className="form-control"
                  placeholder={`Search ${view === "resolved" ? "Resolved" : ""} Faults`}
                  value={searchTerm}
                  onChange={e => setSearchTerm(e.target.value)}
                  aria-label="Search faults"
                />
              </Col>
            </Row>

            <div className="mb-2 px-3"><strong>Total {view === "resolved" ? "Resolved " : ""}Faults: </strong>{filteredFaults.length}</div>

            <Row>
              <Card className="shadow-sm w-100" style={{ minWidth: 0 }}>
                <Card.Body className="p-0 d-flex flex-column">
                  <Table striped bordered hover responsive className="table-fixed-header table-fit mb-0 flex-grow-1 align-middle custom-align-table" aria-label="Faults Table">
                    <colgroup>
                      <col style={{ width: "3.5%", textAlign: "center" }} />
                      <col style={{ width: "6%" }} />
                      <col style={{ width: "6%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "18%" }} />
                      <col style={{ width: "7%" }} />
                      <col style={{ width: "10%" }} />
                      <col style={{ width: "12%" }} />
                    </colgroup>
                    <thead className="sticky-top bg-light">
                      <tr>
                        <th className="text-center">ID</th>
                        <th className="text-center">System ID</th>
                        <th className="text-center">Section ID</th>
                        <th>Reported By</th>
                        <th>Location</th>
                        <th>Description</th>
                        <th className="text-center">Status</th>
                        <th>Assigned To</th>
                        <th>Reported At</th>
                      </tr>
                    </thead>
                    <tbody>
                      {currentFaults.length === 0 ? (
                        <tr><td colSpan={9} className="text-center text-muted py-4">No faults.</td></tr>
                      ) : (
                        currentFaults.map(fault => (
                          <tr key={fault.id} className="table-row-hover">
                            <td className="text-center">{fault.id}</td>
                            <td className="text-center">{fault.SystemID}</td>
                            <td className="text-center">{fault.SectionID}</td>
                            <td>{fault.ReportedBy}</td>
                            <td>{fault.Location}</td>
                            <td className="description-col">{fault.DescFault}</td>
                            <td className="text-center">{fault.Status}</td>
                            <td>{fault.AssignTo}</td>
                            <td style={{ whiteSpace: "nowrap" }}>{fault.DateTime ? new Date(fault.DateTime).toLocaleString() : ""}</td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </Table>

                  {/* Pagination */}
                  <nav aria-label="Fault pagination" className="mt-3 px-3" style={{ flexShrink: 0 }}>
                    <ul className="pagination justify-content-center mb-0">
                      <li className={`page-item ${currentPage === 1 ? "disabled" : ""}`}>
                        <button className="page-link" onClick={() => setCurrentPage(Math.max(currentPage - 1, 1))} aria-label="Previous page">Previous</button>
                      </li>
                      {[...Array(totalPages)].map((_, idx) => {
                        const number = idx + 1;
                        return (
                          <li key={number} className={`page-item ${number === currentPage ? "active" : ""}`}>
                            <button className="page-link" onClick={() => setCurrentPage(number)} aria-current={number === currentPage ? "page" : undefined}>{number}</button>
                          </li>
                        );
                      })}
                      <li className={`page-item ${currentPage === totalPages ? "disabled" : ""}`}>
                        <button className="page-link" onClick={() => setCurrentPage(Math.min(currentPage + 1, totalPages))} aria-label="Next page">Next</button>
                      </li>
                    </ul>
                  </nav>

                </Card.Body>
              </Card>
            </Row>
          </Col>
        </Row>
      </Container>

      {/* Footer */}
      <footer className="fixed-bottom text-white py-2 px-3 d-flex flex-column flex-sm-row justify-content-between align-items-center shadow" style={{ backgroundColor: "#001f3f" }}>
        <div className="mb-2 mb-sm-0">
          <Button className="glass-button" size="sm" onClick={() => alert("Contact support at support@nfm.lk")}>Support</Button>
        </div>
        <div className="text-center flex-grow-1 mb-2 mb-sm-0" aria-live="polite">
          Total Faults: {faults.length} | Unread Notifications: {notifications.filter(n => !n.isRead).length}
        </div>
        <div className="text-center text-sm-end">
          <Button className="glass-button" size="sm" onClick={() => setShowFooterInfo(!showFooterInfo)}>{showFooterInfo ? "Hide Info" : "Show Info"}</Button>
          {showFooterInfo && <div className="mt-1" style={{ fontSize: "0.75rem", opacity: 0.8 }}>Â© 2025 Network Fault Management System. All rights reserved.</div>}
        </div>
      </footer>

      {/* New Fault Modal */}
      <NewFaultModal
        show={showNewFaultModal}
        handleClose={() => setShowNewFaultModal(false)}
        handleAdd={async (data) => {
          try {
            const token = localStorage.getItem('token');
            if (!token) throw new Error("Authentication required.");
            const res = await fetch('http://localhost:5000/api/faults', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`
              },
              body: JSON.stringify({
                SystemID: parseInt(data.SystemID),
                Location: data.Location,
                LocFaultID: null,
                DescFault: data.DescFault,
                ReportedBy: data.ReportedBy,
                ExtNo: null,
                AssignTo: data.AssignTo,
                Status: data.Status,
                SectionID: parseInt(data.SectionID),
                FaultForwardID: null,
              })
            });
            const result = await res.json();
            if (!res.ok) throw new Error(result.message || "Failed to create fault");
            setFaults((prev) => [...prev, result.fault]);
            if(result.fault.Status?.toLowerCase() === "closed"){
              setResolvedFaults((prev) => [...prev, result.fault]);
            }
            return true;
          } catch (err) {
            setError(err.message);
            return false;
          }
        }}
        assignablePersons={assignablePersons}
      />

      {/* Styles omitted for brevity - include your existing styles here */}
    </>
  );
}
