export default function Dashboard({
  userInfo,
  notifications,
  setNotifications,
  onLogout,
}) {
  const [modal, setModal] = useState(false);
  const [edit, setEdit] = useState(null);
  const [search, setSearch] = useState("");
  const [view, setView] = useState(""); // 'faults' or 'resolved'
  const [showNotif, setShowNotif] = useState(false);
  const [notesModal, setNotesModal] = useState(false);
  const [selectedFaultForNotes, setSelectedFaultForNotes] = useState(null);
  const notifRef = useRef();
  const [footerInfo, setFooterInfo] = useState(false);

  const { open, resolved, create, update, remove, resolve, err, setErr } =
    useMultiFaults();

  // Initialize notes hook
  const token = localStorage.getItem("token");
  const {
    notes,
    loading,
    error: notesError,
    fetchNotes,
    addNote,
    editNote,
    deleteNote,
    clearNotesCache,
  } = useFaultNotes(token);

  useEffect(() => {
    if (showNotif)
      setNotifications((n) => n.map((e) => ({ ...e, isRead: true })));
    function outside(e) {
      if (notifRef.current && !notifRef.current.contains(e.target))
        setShowNotif(false);
    }
    document.addEventListener("mousedown", outside);
    return () => document.removeEventListener("mousedown", outside);
  }, [showNotif, setNotifications]);

  const currentFaultArr = view === "faults" ? open : resolved;

  // Sort faults by descending id (Newest first)
  const sortedFaults = useMemo(
    () => [...currentFaultArr].sort((a, b) => b.id - a.id),
    [currentFaultArr]
  );

  // Filter by search term
  const filtered = useMemo(() => {
    return sortedFaults.filter((f) => {
      if (!f) return false;
      const haystack = [
        f.DescFault,
        f.Location,
        f.LocationOfFault,
        f.ReportedBy,
        f.SystemID,
      ]
        .join(" ")
        .toLowerCase();
      return haystack.includes(search.toLowerCase());
    });
  }, [sortedFaults, search]);

  // Pagination state
  const [page, setPage] = useState(1);
  useEffect(() => {
    setPage(1); // Reset to first page when filtered data changes
  }, [filtered]);
  const max = Math.ceil(filtered.length / 10);
  const current = filtered.slice((page - 1) * 10, page * 10);

  // Open modal in edit mode
  function openEditModal(fault) {
    setEdit(fault);
    setModal(true);
  }

  // Open notes modal
  function openNotesModal(fault) {
    setSelectedFaultForNotes(fault);
    setNotesModal(true);
  }

  // Debugging: Log the filtered and current faults to verify data
  useEffect(() => {
    console.log("Filtered faults:", filtered);
    console.log("Current faults:", current);
  }, [filtered, current]);

  return (
    <>
      {/* [Rest of the existing JSX remains unchanged until the FaultsTable rendering] */}

      <Tab
        eventKey="faults"
        title={
          <span className="tab-title-lg">ðŸš§ Faults Review Panel</span>
        }
      >
        {view === "faults" && (
          <>
            <Row className="mb-3 px-3">
              <Col md={4} className="mb-2">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search faults..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  aria-label="Search faults"
                />
              </Col>
            </Row>
            <div className="mb-2 px-3">
              <strong>Total Faults:</strong> {filtered.length}
            </div>
            <FaultsTable
              faults={current}
              onEdit={update}
              onMarkResolved={resolve}
              isResolved={false}
              page={page}
              setPage={setPage}
              max={max}
              onOpenEditModal={openEditModal}
              onOpenNotesModal={openNotesModal}
            />
            {/* Add a check to display all faults if pagination is the issue */}
            {filtered.length > current.length && (
              <div className="text-center mt-3">
                <Button
                  variant="outline-primary"
                  onClick={() => setPage(1)} // Reset to first page to show all
                >
                  Show All Faults
                </Button>
              </div>
            )}
          </>
        )}
      </Tab>

      {/* [Rest of the existing JSX remains unchanged] */}
    </>
  );
}
