import React, { useState, useEffect } from "react";
import { Modal, Button, Form, Alert, Spinner } from "react-bootstrap";

export default function NewFaultModal({
  show,
  handleClose,
  handleAdd,
  assignablePersons = [],
  initialData = null,
}) {
  const [formData, setFormData] = useState({
    systemID: "",
    sectionID: "",
    reportedBy: "",
    location: "",
    description: "",
    urgency: "low",
    status: "open",
    assignedTo: assignablePersons.length > 0 ? assignablePersons[0] : "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [validated, setValidated] = useState(false);

  useEffect(() => {
    const emptyForm = {
      systemID: "",
      sectionID: "",
      reportedBy: "",
      location: "",
      description: "",
      urgency: "low",
      status: "open",
      assignedTo: assignablePersons.length > 0 ? assignablePersons[0] : "",
    };

    if (initialData) {
      setFormData({
        ...emptyForm,
        ...initialData,
        assignedTo: initialData.assignedTo || 
                  (assignablePersons.length > 0 ? assignablePersons[0] : "")
      });
    } else {
      setFormData(emptyForm);
    }
    
    setValidated(false);
    setError("");
    setIsSubmitting(false);
  }, [initialData, assignablePersons, show]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
    
    if (error) {
      setError("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    const form = e.currentTarget;
    
    if (form.checkValidity() === false) {
      setValidated(true);
      return;
    }

    setIsSubmitting(true);
    setError("");

    try {
      const success = await handleAdd(formData);
      if (success) {
        handleClose();
        setFormData({
          systemID: "",
          sectionID: "",
          reportedBy: "",
          location: "",
          description: "",
          urgency: "low",
          status: "open",
          assignedTo: assignablePersons.length > 0 ? assignablePersons[0] : "",
        });
        setValidated(false);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      setError("Failed to submit form. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleModalClose = () => {
    if (!isSubmitting) {
      handleClose();
    }
  };

  return (
    <Modal show={show} onHide={handleModalClose} centered backdrop={isSubmitting ? "static" : true}>
      <Modal.Header closeButton={!isSubmitting}>
        <Modal.Title>
          {initialData ? "Edit Fault Report" : "New Fault Report"}
        </Modal.Title>
      </Modal.Header>
      
      <Modal.Body>
        {error && (
          <Alert variant="danger" dismissible onClose={() => setError("")}>
            {error}
          </Alert>
        )}
        
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-6 mb-3">
              <Form.Group controlId="formSystemID">
                <Form.Label>System ID <span className="text-danger">*</span></Form.Label>
                <Form.Control
                  type="number"
                  name="systemID"
                  value={formData.systemID}
                  onChange={handleChange}
                  placeholder="Enter system ID"
                  required
                  min="1"
                  disabled={isSubmitting}
                />
                <Form.Control.Feedback type="invalid">
                  Please provide a valid system ID.
                </Form.Control.Feedback>
              </Form.Group>
            </div>

            <div className="col-md-6 mb-3">
              <Form.Group controlId="formSectionID">
                <Form.Label>Section ID <span className="text-danger">*</span></Form.Label>
                <Form.Control
                  type="number"
                  name="sectionID"
                  value={formData.sectionID}
                  onChange={handleChange}
                  placeholder="Enter section ID"
                  required
                  min="1"
                  disabled={isSubmitting}
                />
                <Form.Control.Feedback type="invalid">
                  Please provide a valid section ID.
                </Form.Control.Feedback>
              </Form.Group>
            </div>
          </div>

          <Form.Group className="mb-3" controlId="formReportedBy">
            <Form.Label>Reported By <span className="text-danger">*</span></Form.Label>
            <Form.Control
              type="text"
              name="reportedBy"
              value={formData.reportedBy}
              onChange={handleChange}
              placeholder="Enter reporter name"
              required
              maxLength="100"
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">
              Please provide the reporter's name.
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formLocation">
            <Form.Label>Location <span className="text-danger">*</span></Form.Label>
            <Form.Control
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              placeholder="Enter location"
              required
              maxLength="200"
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">
              Please provide the location.
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formDescription">
            <Form.Label>Description <span className="text-danger">*</span></Form.Label>
            <Form.Control
              name="description"
              as="textarea"
              rows={3}
              value={formData.description}
              onChange={handleChange}
              placeholder="Describe the fault in detail"
              required
              maxLength="500"
              disabled={isSubmitting}
            />
            <Form.Control.Feedback type="invalid">
              Please provide a description of the fault.
            </Form.Control.Feedback>
            <Form.Text className="text-muted">
              {formData.description.length}/500 characters
            </Form.Text>
          </Form.Group>

          <div className="row">
            <div className="col-md-4 mb-3">
              <Form.Group controlId="formUrgency">
                <Form.Label>Urgency <span className="text-danger">*</span></Form.Label>
                <Form.Select
                  name="urgency"
                  value={formData.urgency}
                  onChange={handleChange}
                  required
                  disabled={isSubmitting}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  Please select an urgency level.
                </Form.Control.Feedback>
              </Form.Group>
            </div>

            {initialData && (
              <div className="col-md-4 mb-3">
                <Form.Group controlId="formStatus">
                  <Form.Label>Status <span className="text-danger">*</span></Form.Label>
                  <Form.Select
                    name="status"
                    value={formData.status}
                    onChange={handleChange}
                    required
                    disabled={isSubmitting}
                  >
                    <option value="open">Open</option>
                    <option value="pending">Pending</option>
                    <option value="ongoing">Ongoing</option>
                    <option value="closed">Closed</option>
                  </Form.Select>
                  <Form.Control.Feedback type="invalid">
                    Please select a status.
                  </Form.Control.Feedback>
                </Form.Group>
              </div>
            )}

            <div className={`col-md-${initialData ? '4' : '8'} mb-3`}>
              <Form.Group controlId="formAssignedTo">
                <Form.Label>Assigned To <span className="text-danger">*</span></Form.Label>
                <Form.Select
                  name="assignedTo"
                  value={formData.assignedTo}
                  onChange={handleChange}
                  required
                  disabled={assignablePersons.length === 0 || isSubmitting}
                >
                  {assignablePersons.length === 0 ? (
                    <option value="" disabled>No persons available</option>
                  ) : (
                    assignablePersons.map((person) => (
                      <option key={person} value={person}>
                        {person}
                      </option>
                    ))
                  )}
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  Please select an assignee.
                </Form.Control.Feedback>
              </Form.Group>
            </div>
          </div>

          <Modal.Footer className="px-0">
            <Button 
              variant="secondary" 
              onClick={handleModalClose}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button 
              variant="primary" 
              type="submit"
              disabled={isSubmitting || assignablePersons.length === 0}
            >
              {isSubmitting ? (
                <>
                  <Spinner as="span" animation="border" size="sm" className="me-2" />
                  {initialData ? "Updating..." : "Creating..."}
                </>
              ) : (
                initialData ? "Update Fault" : "Create Fault"
              )}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal.Body>
    </Modal>
  );
}
