import React, { useEffect, useRef, useState, useMemo } from "react";
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Card,
  Tabs,
  Tab,
  Modal,
  Form,
} from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";
import NewFaultModal from "./NewFaultModal";
import Activecharts from "./components/Activecharts";

const assignablePersons = [
  "John Doe",
  "Jane Smith",
  "Alex Johnson",
  "Emily Davis",
];

// Custom hook managing faults and notes fetching/updating
function useMultiFaults(userId) {
  const [open, setOpen] = useState([]);
  const [resolved, setResolved] = useState([]);
  const [notesByFault, setNotesByFault] = useState({}); // { faultId: [note, ...] }
  const [err, setErr] = useState("");
  const token = localStorage.getItem("token");

  // Fetch open faults and their notes
  const fetchOpen = async () => {
    if (!token) return setErr("No authentication token.");
    try {
      const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults?status=open`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to fetch open faults");
      const data = await res.json();
      setOpen(data);
      setErr("");
      await fetchNotesForFaults(data);
    } catch (e) {
      setErr(e.message);
    }
  };

  // Fetch resolved faults and their notes
  const fetchResolved = async () => {
    if (!token) return setErr("No authentication token.");
    try {
      const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults?status=closed`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to fetch resolved faults");
      const data = await res.json();
      const closedFaults = data.filter((f) => f.Status.toLowerCase() === "closed");
      setResolved(closedFaults);
      setErr("");
      await fetchNotesForFaults(closedFaults);
    } catch (e) {
      setErr(e.message);
    }
  };

  // Fetch notes for an array of faults, grouped by fault ID
  const fetchNotesForFaults = async (faultList) => {
    if (!faultList || faultList.length === 0) return;
    try {
      const allNotes = {};
      await Promise.all(
        faultList.map(async (fault) => {
          const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/notes?faultId=${fault.id}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          if (!res.ok) throw new Error(`Failed to fetch notes for fault ${fault.id}`);
          const notes = await res.json();
          allNotes[fault.id] = notes;
        })
      );
      setNotesByFault((prev) => ({ ...prev, ...allNotes }));
    } catch (e) {
      console.error(e.message);
    }
  };

  // Create a new fault
  const create = async (data) => {
    if (!token) throw new Error("Authentication required.");
    const resp = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults`, {
      method: "POST",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify(data),
    });
    if (!resp.ok) {
      const error = await resp.json().catch(() => ({}));
      throw new Error(error.message || "Failed to create fault");
    }
    const result = await resp.json();
    if (result.fault.Status.toLowerCase() === "closed") {
      setResolved((r) => [...r, result.fault]);
    } else {
      setOpen((o) => [...o, result.fault]);
    }
    return true;
  };

  // Update a fault
  const update = async (data) => {
    if (!token) throw new Error("Authentication required.");
    const dataToSend = { ...data };
    if (dataToSend.SectionID === "") dataToSend.SectionID = null;
    const resp = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults/${data.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify(dataToSend),
    });
    if (!resp.ok) {
      const error = await resp.json().catch(() => ({}));
      throw new Error(error.message || "Failed to update fault");
    }
    const result = await resp.json();
    const updatedFault = result.fault;

    if (updatedFault.Status.toLowerCase() === "closed") {
      setOpen((o) => o.filter((f) => f.id !== updatedFault.id));
      setResolved((r) => {
        const exists = r.some((f) => f.id === updatedFault.id);
        if (exists) return r.map((f) => (f.id === updatedFault.id ? updatedFault : f));
        return [...r, updatedFault];
      });
    } else {
      setOpen((o) => o.map((f) => (f.id === updatedFault.id ? updatedFault : f)));
      setResolved((r) => r.filter((f) => f.id !== updatedFault.id));
    }
    return true;
  };

  // Remove a fault
  const remove = async (id) => {
    if (!token) return;
    const resp = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    if (!resp.ok) {
      const error = await resp.json().catch(() => ({}));
      throw new Error(error.message || "Failed to delete fault");
    }
    setOpen((o) => o.filter((f) => f.id !== id));
    setResolved((r) => r.filter((f) => f.id !== id));
  };

  // Mark fault as resolved
  const resolve = async (id) => {
    if (!token) return setErr("No authentication token.");
    try {
      const fault = open.find((f) => f.id === id);
      if (!fault) throw new Error("Fault not found in open list.");

      const payload = {
        SystemID: fault.SystemID,
        Location: fault.Location,
        LocationOfFault: fault.LocationOfFault,
        DescFault: fault.DescFault,
        ReportedBy: fault.ReportedBy,
        AssignTo: fault.AssignTo,
        SectionID: fault.SectionID,
        Status: "Closed",
      };

      const resp = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/faults/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });

      if (!resp.ok) {
        const errorData = await resp.json().catch(() => ({}));
        throw new Error(errorData.message || "Failed to mark as resolved");
      }
      const result = await resp.json();
      setOpen((o) => o.filter((f) => f.id !== id));
      setResolved((r) => {
        if (r.some((f) => f.id === id)) return r;
        return [...r, result.fault];
      });
    } catch (error) {
      setErr(error.message);
    }
  };

  // Save a note linked to a fault (new API)
  const saveNote = async (faultId, noteText) => {
    if (!token) throw new Error("Authentication required.");
    if (!userId) throw new Error("User ID required for note saving");

    const resp = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/notes`, {
      method: "POST",
      headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
      body: JSON.stringify({ FaultID: faultId, UserID: userId, Notes: noteText }),
    });
    if (!resp.ok) {
      const error = await resp.json().catch(() => ({}));
      throw new Error(error.message || "Failed to save note");
    }
    const newNote = await resp.json();

    setNotesByFault((prev) => {
      const existingNotes = prev[faultId] || [];
      return { ...prev, [faultId]: [...existingNotes, newNote] };
    });

    return true;
  };

  // Initial fetch
  useEffect(() => {
    fetchOpen();
    fetchResolved();
  }, []);

  return {
    open,
    resolved,
    notesByFault,
    create,
    update,
    remove,
    resolve,
    saveNote,
    err,
    setErr,
  };
}

// Table component listing faults, showing latest note and allowing note add/edit
function FaultsTable({
  faults,
  notesByFault,
  onEdit,
  onSaveNote,
  onDelete,
  onMarkResolved,
  isResolved,
  page,
  setPage,
  max,
  onOpenEditModal,
}) {
  const [noteModal, setNoteModal] = useState(false);
  const [selectedFault, setSelectedFault] = useState(null);
  const [noteText, setNoteText] = useState("");

  // Open modal for note editing/adding
  const openNoteModal = (fault) => {
    setSelectedFault(fault);
    const faultNotes = notesByFault[fault.id] || [];
    setNoteText(faultNotes.length > 0 ? faultNotes[faultNotes.length - 1].Notes : "");
    setNoteModal(true);
  };

  // Save note handler
  const handleSaveNote = async () => {
    if (!selectedFault) return;
    try {
      await onSaveNote(selectedFault.id, noteText);
      setNoteModal(false);
      setSelectedFault(null);
      setNoteText("");
    } catch (err) {
      alert("Failed to save note: " + err.message);
    }
  };

  return (
    <Row style={{ height: "calc(100vh - 60px - 130px - 80px)", overflowY: "auto" }}>
      <Card
        className="glass-card w-100"
        style={{
          minWidth: 0,
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(10px)",
          border: "1px solid rgba(255, 255, 255, 0.2)",
          borderRadius: "20px",
          boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.1)",
        }}
      >
        <Card.Body className="p-3 d-flex flex-column">
          <Table
            responsive
            className="table-fixed-header table-fit mb-0 flex-grow-1 align-middle custom-align-table table-borderless glass-table"
            aria-label="Faults Table"
          >
            <colgroup>
              <col style={{ width: "3.5%", textAlign: "center" }} />
              <col style={{ width: "6%" }} />
              <col style={{ width: "10%" }} />
              <col style={{ width: "10%" }} />
              <col style={{ width: "10%" }} />
              <col style={{ width: "18%" }} />
              <col style={{ width: "7%" }} />
              <col style={{ width: "10%" }} />
              <col style={{ width: "7.5%" }} />
              {!isResolved && <col style={{ width: "5%" }} />}
              <col style={{ width: "10%" }} />
            </colgroup>
            <thead className="sticky-top bg-light">
              <tr>
                <th className="text-center">ID</th>
                <th className="text-center">Systems</th>
                <th>Reported By</th>
                <th>Location</th>
                <th>Location of Fault</th>
                <th>Description</th>
                <th className="text-center">Status</th>
                <th>Assigned To</th>
                <th>Reported At</th>
                {!isResolved && <th className="text-center">Actions</th>}
                <th>Remarks</th>
              </tr>
            </thead>
            <tbody>
              {faults.length === 0 ? (
                <tr>
                  <td colSpan={isResolved ? 10 : 11} className="text-center text-muted py-4">
                    No faults.
                  </td>
                </tr>
              ) : (
                faults.map((f) => {
                  const faultNotes = notesByFault[f.id] || [];
                  const latestNote = faultNotes.length > 0 ? faultNotes[faultNotes.length - 1].Notes : null;

                  return (
                    <tr
                      key={f.id}
                      className={`table-row-hover ${
                        f.Status === "In Progress"
                          ? "status-in-progress-row"
                          : f.Status === "Pending"
                          ? "status-pending-row"
                          : f.Status === "Closed"
                          ? "status-closed-row"
                          : ""
                      }`}
                    >
                      <td className="text-center">{f.id}</td>
                      <td className="text-center">{f.SystemID}</td>
                      <td>{f.ReportedBy}</td>
                      <td>{f.Location}</td>
                      <td>{f.LocationOfFault}</td>
                      <td className="description-col">{f.DescFault}</td>
                      <td>
                        <select
                          value={f.Status}
                          onChange={async (e) => {
                            if (isResolved) return;
                            const updatedFault = { ...f, Status: e.target.value };
                            try {
                              await onEdit(updatedFault);
                            } catch (err) {
                              alert("Failed to update status: " + err.message);
                            }
                          }}
                          className={`form-select form-select-sm status-${f.Status.toLowerCase().replace(/\s+/g, "-")}`}
                          disabled={isResolved}
                          aria-label={`Change status for fault ${f.id}`}
                          style={{
                            backgroundColor:
                              f.Status === "In Progress"
                                ? "#fff3cd"
                                : f.Status === "Pending"
                                ? "#cff4fc"
                                : f.Status === "Closed"
                                ? "#d1e7dd"
                                : "",
                            color: "#000",
                            fontWeight: "500",
                          }}
                        >
                          <option value="In Progress">In Progress</option>
                          <option value="Pending">Pending</option>
                          <option value="Closed">Closed</option>
                        </select>
                      </td>
                      <td>{f.AssignTo}</td>
                      <td style={{ whiteSpace: "nowrap" }}>
                        {f.DateTime ? new Date(f.DateTime).toLocaleString() : ""}
                      </td>
                      {!isResolved && (
                        <td className="text-center">
                          <Button
                            variant="outline-primary"
                            size="sm"
                            className="me-1 mb-1"
                            onClick={() => onOpenEditModal(f)}
                            aria-label={`Edit fault ${f.id}`}
                          >
                            Edit
                          </Button>
                        </td>
                      )}
                      <td>
                        <div className="d-flex align-items-start gap-2">
                          <div style={{ flex: 1 }}>
                            {latestNote ? (
                              <div
                                className="text-muted"
                                style={{
                                  fontSize: "0.9rem",
                                  maxHeight: "50px",
                                  overflowY: "auto",
                                }}
                              >
                                {latestNote}
                              </div>
                            ) : (
                              <div className="text-muted fst-italic" style={{ fontSize: "0.9rem" }}>
                                No notes
                              </div>
                            )}
                          </div>
                          {!isResolved && (
                            <Button
                              variant="outline-primary"
                              size="sm"
                              onClick={() => openNoteModal(f)}
                              className="px-2 py-1"
                              style={{ whiteSpace: "nowrap", fontSize: "0.85rem" }}
                            >
                              {latestNote ? "✏️ Edit" : "+ Add Note"}
                            </Button>
                          )}
                        </div>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </Table>

          {/* Pagination */}
          <nav aria-label="Fault pagination" className="mt-3 px-3" style={{ flexShrink: 0 }}>
            <ul className="pagination justify-content-center mb-0">
              <li className={`page-item ${page === 1 ? "disabled" : ""}`}>
                <button
                  className="page-link"
                  onClick={() => setPage(Math.max(page - 1, 1))}
                  aria-label="Previous page"
                >
                  Previous
                </button>
              </li>
              {Array.from({ length: max }).map((_, idx) => {
                const pageNum = idx + 1;
                return (
                  <li key={pageNum} className={`page-item ${page === pageNum ? "active" : ""}`}>
                    <button
                      className="page-link"
                      onClick={() => setPage(pageNum)}
                      aria-current={page === pageNum ? "page" : undefined}
                    >
                      {pageNum}
                    </button>
                  </li>
                );
              })}
              <li className={`page-item ${page === max || max === 0 ? "disabled" : ""}`}>
                <button
                  className="page-link"
                  onClick={() => setPage(Math.min(page + 1, max))}
                  aria-label="Next page"
                >
                  Next
                </button>
              </li>
            </ul>
          </nav>
        </Card.Body>
      </Card>

      {/* Notes Modal */}
      <Modal show={noteModal} onHide={() => setNoteModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedFault?.Remarks ? "Edit Note" : "Add Note"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Control
              as="textarea"
              rows={4}
              value={noteText}
              onChange={(e) => setNoteText(e.target.value)}
              placeholder="Enter your notes here..."
              style={{ fontSize: "0.9rem" }}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setNoteModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSaveNote}>
            Save Note
          </Button>
        </Modal.Footer>
      </Modal>
    </Row>
  );
}

// Main Dashboard Component
export default function Dashboard({ userInfo, notifications, setNotifications, onLogout }) {
  const [modal, setModal] = useState(false);
  const [edit, setEdit] = useState(null);
  const [search, setSearch] = useState("");
  const [view, setView] = useState(""); // 'faults' or 'resolved'
  const [showNotif, setShowNotif] = useState(false);
  const notifRef = useRef();
  const [footerInfo, setFooterInfo] = useState(false);

  const {
    open,
    resolved,
    notesByFault,
    create,
    update,
    remove,
    resolve,
    saveNote,
    err,
    setErr,
  } = useMultiFaults(userInfo?.id);

  useEffect(() => {
    if (showNotif)
      setNotifications((n) => n.map((e) => ({ ...e, isRead: true })));
    function outside(e) {
      if (notifRef.current && !notifRef.current.contains(e.target))
        setShowNotif(false);
    }
    document.addEventListener("mousedown", outside);
    return () => document.removeEventListener("mousedown", outside);
  }, [showNotif, setNotifications]);

  const currentFaultArr = view === "faults" ? open : resolved;

  // Sort faults by descending id (Newest first)
  const sortedFaults = useMemo(() => [...currentFaultArr].sort((a, b) => b.id - a.id), [currentFaultArr]);

  // Filter by search term
  const filtered = useMemo(() => {
    return sortedFaults.filter((f) => {
      if (!f) return false;
      const haystack = [f.DescFault, f.Location, f.LocationOfFault, f.ReportedBy, f.SystemID]
        .join(" ")
        .toLowerCase();
      return haystack.includes(search.toLowerCase());
    });
  }, [sortedFaults, search]);

  // Pagination state
  const [page, setPage] = useState(1);
  useEffect(() => {
    setPage(1);
  }, [filtered]);
  const max = Math.ceil(filtered.length / 10);
  const current = filtered.slice((page - 1) * 10, page * 10);

  function openEditModal(fault) {
    setEdit(fault);
    setModal(true);
  }

  return (
    <>
      {/* Navbar omitted for brevity, same as your original code */}

      <Container fluid className="pt-5 mt-4">
        {err && (
          <div className="alert alert-danger" role="alert">
            {err}{" "}
            <button type="button" className="btn-close float-end" onClick={() => setErr("")} aria-label="Close" />
          </div>
        )}
        <Row>
          {/* Sidebar omitted for brevity, same as your original code */}

          <Col
            className="ms-auto d-flex flex-column"
            style={{
              marginLeft: "16.666667%",
              width: "calc(100% - 16.666667%)",
              height: "calc(100vh - 60px)",
              overflow: "hidden",
              paddingLeft: 0,
              maxWidth: "82%",
            }}
          >
            {!view ? (
              <div className="d-flex justify-content-center align-items-center h-100">
                <div className="text-center text-muted">
                  <h2 className="mb-4">👋 Welcome to NFM System</h2>
                  <p className="lead">Please select an option from the sidebar to get started.</p>
                </div>
              </div>
            ) : (
              <Tabs activeKey={view} className="custom-tabs" justify>
                <Tab eventKey="faults" title={<span className="tab-title-lg">🚧 Faults Review Panel</span>}>
                  {view === "faults" && (
                    <>
                      <Row className="mb-3 px-3">
                        <Col md={4} className="mb-2">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Search faults..."
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                            aria-label="Search faults"
                          />
                        </Col>
                      </Row>
                      <div className="mb-2 px-3">
                        <strong>Total Faults:</strong> {filtered.length}
                      </div>
                      <FaultsTable
                        faults={current}
                        notesByFault={notesByFault}
                        onEdit={update}
                        onSaveNote={saveNote}
                        onMarkResolved={resolve}
                        isResolved={false}
                        page={page}
                        setPage={setPage}
                        max={max}
                        onOpenEditModal={openEditModal}
                      />
                    </>
                  )}
                </Tab>

                <Tab eventKey="resolved" title={<span className="tab-title-lg">✅ Resolved Faults</span>}>
                  {view === "resolved" && (
                    <>
                      <Row className="mb-3 px-3">
                        <Col md={4} className="mb-2">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Search..."
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                            aria-label="Search resolved faults"
                          />
                        </Col>
                      </Row>
                      <div className="mb-2 px-3">
                        <strong>Total Resolved Faults:</strong> {filtered.length}
                      </div>
                      <FaultsTable
                        faults={current}
                        notesByFault={notesByFault}
                        isResolved={true}
                        page={page}
                        setPage={setPage}
                        max={max}
                      />
                    </>
                  )}
                </Tab>

                <Tab eventKey="active-chart" title={<span className="tab-title-lg">📊 Active Chart</span>}>
                  {view === "active-chart" && <Activecharts faults={[...open, ...resolved]} />}
                </Tab>

                <Tab eventKey="user-performance" title={<span className="tab-title-lg">👥 User Performance</span>}>
                  {view === "user-performance" && (
                    <div className="p-4">
                      <h3>User Performance</h3>
                      <p>User performance metrics and analytics will be displayed here.</p>
                    </div>
                  )}
                </Tab>
              </Tabs>
            )}
          </Col>
        </Row>
      </Container>

      {/* Footer and NewFaultModal omitted for brevity but should be the same as your original code */}
      <NewFaultModal
        show={modal}
        handleClose={() => {
          setModal(false);
          setEdit(null);
        }}
        handleAdd={edit ? update : create}
        assignablePersons={assignablePersons}
        initialData={edit}
      />

      {/* Include your original styles here */}
    </>
  );
}
