import React, { useState } from "react";
import { Card, Button, Form, InputGroup } from "react-bootstrap";
import { Eye, EyeSlash } from "react-bootstrap-icons";
import { useNavigate } from "react-router-dom";

const API_BASE_URL = "http://localhost:5000/api/auth";

export default function LoginPage({ onLogin, onRegisterClick }) {
  const [step, setStep] = useState(1); // 1 = username, 2 = password
  const [usernameInput, setUsernameInput] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleUsernameSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    const username = usernameInput.trim();

    if (!username) {
      setError("Please enter your username");
      return;
    }

    setLoading(true);
    try {
      // Check if username exists (mock API call)
      const response = await fetch(`${API_BASE_URL}/check-username`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username }),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || 'Username check failed');
      }

      if (result.exists) {
        setStep(2);
      } else {
        setError("User not found");
      }
    } catch (err) {
      console.error('Username check error:', err);
      setError(err.message || "Failed to check username");
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!password.trim()) {
      setError("Please enter your password");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: usernameInput.trim(),
          password,
        }),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || 'Login failed');
      }

      // Store token and user data
      localStorage.setItem('token', result.token);
      localStorage.setItem('user', JSON.stringify(result.user));

      // Call parent callback if provided
      if (onLogin) {
        onLogin({
          token: result.token,
          user: result.user
        });
      }

      // Navigate to dashboard
      navigate('/dashboard');

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message.includes('401') ? "Incorrect password" : err.message || "Login failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#0b1e39",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        padding: "1rem",
      }}
    >
      <Card
        style={{
          maxWidth: "400px",
          width: "100%",
          backgroundColor: "#12345b",
          borderRadius: "16px",
          padding: "2rem",
          color: "white",
          boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
          textAlign: "center",
        }}
      >
        {step === 1 && (
          <>
            <h3 className="mb-4">Welcome!<br />What's your username?</h3>
            
            {error && (
              <div className="mb-3 text-danger" style={{ minHeight: '24px' }}>
                {error}
              </div>
            )}

            <Form onSubmit={handleUsernameSubmit}>
              <Form.Group className="mb-3">
                <Form.Control
                  type="text"
                  placeholder="Enter username"
                  value={usernameInput}
                  onChange={(e) => setUsernameInput(e.target.value.trimStart())}
                  autoFocus
                  disabled={loading}
                  style={{
                    backgroundColor: "#102c4a",
                    borderColor: "#345b96",
                    color: "white",
                    textAlign: "center",
                  }}
                />
              </Form.Group>
              <Button
                type="submit"
                variant="primary"
                style={{ borderRadius: "8px", width: "100%" }}
                disabled={loading}
              >
                {loading ? 'Checking...' : 'Next'}
              </Button>
            </Form>

            {onRegisterClick && (
              <Button
                variant="link"
                className="mt-3 text-light"
                onClick={onRegisterClick}
                style={{ 
                  textDecoration: "underline", 
                  fontSize: "0.9rem", 
                  cursor: "pointer" 
                }}
              >
                Register New User
              </Button>
            )}
          </>
        )}

        {step === 2 && (
          <>
            <h3 className="mb-4">Welcome back!<br />Enter your password</h3>
            
            {error && (
              <div className="mb-3 text-danger" style={{ minHeight: '24px' }}>
                {error}
              </div>
            )}

            <Form onSubmit={handlePasswordSubmit}>
              <Form.Group className="mb-3" style={{ textAlign: "left" }}>
                <Form.Label>Password</Form.Label>
                <InputGroup>
                  <Form.Control
                    type={showPassword ? "text" : "password"}
                    placeholder="Enter password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    autoFocus
                    disabled={loading}
                    style={{
                      backgroundColor: "#102c4a",
                      borderColor: "#345b96",
                      color: "white",
                    }}
                  />
                  <InputGroup.Text
                    style={{
                      backgroundColor: "#102c4a",
                      borderColor: "#345b96",
                      cursor: "pointer",
                    }}
                    onClick={() => setShowPassword((v) => !v)}
                    title={showPassword ? "Hide password" : "Show password"}
                  >
                    {showPassword ? (
                      <EyeSlash color="lightgray" />
                    ) : (
                      <Eye color="lightgray" />
                    )}
                  </InputGroup.Text>
                </InputGroup>
              </Form.Group>
              <Button
                type="submit"
                variant="primary"
                style={{ borderRadius: "8px", width: "100%" }}
                disabled={loading}
              >
                {loading ? 'Logging in...' : 'Login'}
              </Button>
            </Form>

            <Button
              variant="link"
              className="mt-3 text-light"
              onClick={() => {
                setStep(1);
                setPassword("");
                setError(null);
              }}
              disabled={loading}
            >
              Change User
            </Button>
          </>
        )}
      </Card>
    </div>
  );
}
