import React, { useState } from "react";
import axios from "axios";
import { Card, Button, Form, InputGroup, Alert } from "react-bootstrap";
import { Eye, EyeSlash } from "react-bootstrap-icons";

const API_BASE_URL = "http://localhost:5000/api/auth"; // Update if different

export default function LoginPage({ onLogin }) {
  const [step, setStep] = useState("login"); // 'login' | 'register'
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);

  const resetForm = () => {
    setUsername("");
    setEmail("");
    setPassword("");
    setRole("user");
    setError(null);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const res = await axios.post(`${API_BASE_URL}/login`, {
        username,
        password,
      }, {
        withCredentials: true,
      });
      onLogin(res.data);
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const res = await axios.post(`${API_BASE_URL}/register`, {
        username,
        email,
        password,
        role,
      });
      alert("Registration successful. You can now log in.");
      resetForm();
      setStep("login");
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#0b1e39",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        padding: "1rem",
      }}
    >
      <Card
        style={{
          maxWidth: "420px",
          width: "100%",
          backgroundColor: "#12345b",
          borderRadius: "16px",
          padding: "2rem",
          color: "white",
          boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
          textAlign: "center",
        }}
      >
        <h3 className="mb-4">{step === "login" ? "Login" : "Register"}</h3>

        {error && <Alert variant="danger">{error}</Alert>}

        <Form onSubmit={step === "login" ? handleLogin : handleRegister}>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              style={{
                backgroundColor: "#102c4a",
                borderColor: "#345b96",
                color: "white",
              }}
            />
          </Form.Group>

          {step === "register" && (
            <Form.Group className="mb-3">
              <Form.Control
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                }}
              />
            </Form.Group>
          )}

          <Form.Group className="mb-3">
            <InputGroup>
              <Form.Control
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                }}
              />
              <InputGroup.Text
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  cursor: "pointer",
                }}
                onClick={() => setShowPassword((v) => !v)}
              >
                {showPassword ? <EyeSlash color="lightgray" /> : <Eye color="lightgray" />}
              </InputGroup.Text>
            </InputGroup>
          </Form.Group>

          {step === "register" && (
            <Form.Group className="mb-3">
              <Form.Select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                style={{
                  backgroundColor: "#102c4a",
                  borderColor: "#345b96",
                  color: "white",
                }}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </Form.Select>
            </Form.Group>
          )}

          <Button
            type="submit"
            variant="primary"
            style={{ borderRadius: "8px", width: "100%" }}
          >
            {step === "login" ? "Login" : "Register"}
          </Button>
        </Form>

        <Button
          variant="link"
          className="mt-3 text-light"
          onClick={() => {
            resetForm();
            setStep(step === "login" ? "register" : "login");
          }}
          style={{ textDecoration: "underline", fontSize: "0.9rem" }}
        >
          {step === "login" ? "Don't have an account? Register" : "Already have an account? Login"}
        </Button>
      </Card>
    </div>
  );
}
