import React, { useState } from "react";
import { Card, Button, Form, InputGroup } from "react-bootstrap";
import { Eye, EyeSlash } from "react-bootstrap-icons";
import { useNavigate } from "react-router-dom";

const API_BASE_URL = "http://localhost:5000/api/auth";

export default function LoginPage({ onLogin, onRegisterClick }) {
  const [step, setStep] = useState(1);
  const [usernameInput, setUsernameInput] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleUsernameSubmit = (e) => {
    e.preventDefault();
    setError(null);
    
    if (!usernameInput.trim()) {
      setError("Please enter your username");
      return;
    }
    
    // Proceed to password step without API check
    setStep(2);
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!password.trim()) {
      setError("Please enter your password");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: usernameInput.trim(),
          password,
        }),
      });

      const result = await response.json();

      if (!response.ok) {
        // More specific error handling
        if (response.status === 401) {
          if (result.message.includes('inactive')) {
            throw new Error('Your account is inactive. Please contact support.');
          } else {
            throw new Error('Invalid username or password');
          }
        }
        throw new Error(result.message || 'Login failed');
      }

      // Store token and user data
      localStorage.setItem('token', result.token);
      localStorage.setItem('user', JSON.stringify(result.user));

      if (onLogin) {
        onLogin({
          token: result.token,
          user: result.user
        });
      }

      navigate('/dashboard');

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ /* your existing styles */ }}>
      <Card style={{ /* your existing styles */ }}>
        {step === 1 && (
          <>
            <h3 className="mb-4">Welcome!<br />What's your username?</h3>
            
            {error && (
              <div className="mb-3 text-danger" style={{ minHeight: '24px' }}>
                {error}
              </div>
            )}

            <Form onSubmit={handleUsernameSubmit}>
              {/* Your existing form fields */}
            </Form>

            {onRegisterClick && (
              <Button
                variant="link"
                className="mt-3 text-light"
                onClick={onRegisterClick}
              >
                Register New User
              </Button>
            )}
          </>
        )}

        {step === 2 && (
          <>
            <h3 className="mb-4">Welcome back!<br />Enter your password</h3>
            
            {error && (
              <div className="mb-3 text-danger" style={{ minHeight: '24px' }}>
                {error}
              </div>
            )}

            <Form onSubmit={handlePasswordSubmit}>
              {/* Your existing password form fields */}
            </Form>

            <Button
              variant="link"
              className="mt-3 text-light"
              onClick={() => setStep(1)}
            >
              Change User
            </Button>
          </>
        )}
      </Card>
    </div>
  );
}
