import React, { useEffect, useState, useRef } from "react";
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Card,
  Tabs,
  Tab,
  Alert,
} from "react-bootstrap";
import { BellFill } from "react-bootstrap-icons";
import UserProfileDisplay from "./UserProfileDisplay";
import NewFaultModal from "./NewFaultModal";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Configure axios defaults
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Add token to requests if available
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle token expiration
axios.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default function Dashboard({
  userInfo,
  notifications,
  setNotifications,
  onLogout,
}) {
  const navigate = useNavigate();
  const [showFooterInfo, setShowFooterInfo] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const notifRef = useRef();
  const [showNewFaultModal, setShowNewFaultModal] = useState(false);
  const [editFault, setEditFault] = useState(null);
  const [faults, setFaults] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const assignablePersons = ["John Doe", "Jane Smith", "Alex Johnson", "Emily Davis"];

  // Helper functions
  const getUrgencyColor = (level) => {
    if (!level) return "light";
    switch (level.toLowerCase()) {
      case "high": return "danger";
      case "medium": return "warning";
      case "low": return "secondary";
      default: return "light";
    }
  };

  const getStatusColor = (status) => {
    if (!status) return "light";
    switch (status.toLowerCase()) {
      case "open": return "primary";
      case "closed": return "success";
      case "pending": return "warning";
      default: return "light";
    }
  };

  // API functions
  const fetchFaults = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await axios.get('/api/faults');
      setFaults(response.data.faults || []);
    } catch (error) {
      console.error('Error fetching faults:', error);
      setError('Failed to fetch faults. Please try again.');
      setFaults([]);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleStatus = async (id) => {
    try {
      const fault = faults.find((f) => f.id === id);
      if (!fault) return;
      
      const newStatus = fault.Status === "Open" ? "Closed" : "Open";
      await axios.put(`/api/faults/${id}`, { Status: newStatus });
      
      setFaults(faults.map(f => 
        f.id === id ? { ...f, Status: newStatus } : f
      ));
    } catch (error) {
      console.error('Error updating fault status:', error);
      setError('Failed to update fault status');
    }
  };

  const handleDeleteFault = async (id) => {
    if (window.confirm("Are you sure you want to delete this fault?")) {
      try {
        await axios.delete(`/api/faults/${id}`);
        setFaults(faults.filter(f => f.id !== id));
      } catch (error) {
        console.error('Error deleting fault:', error);
        setError('Failed to delete fault');
      }
    }
  };

  const handleNewFault = async (faultData) => {
    try {
      const response = await axios.post('/api/faults', {
        systemID: parseInt(faultData.systemID),
        sectionID: parseInt(faultData.sectionID),
        reportedBy: faultData.reportedBy,
        location: faultData.location,
        description: faultData.description,
        urgency: faultData.urgency,
        assignedTo: faultData.assignedTo
      });
      
      setFaults([response.data.fault, ...faults]);
      return true;
    } catch (error) {
      console.error('Error creating fault:', error);
      setError('Failed to create fault');
      return false;
    }
  };

  const handleUpdateFault = async (faultData) => {
    try {
      const response = await axios.put(`/api/faults/${faultData.id}`, {
        SystemID: parseInt(faultData.systemID),
        SectionID: parseInt(faultData.sectionID),
        ReportedBy: faultData.reportedBy,
        Location: faultData.location,
        DescFault: faultData.description,
        Urgency: faultData.urgency,
        AssignTo: faultData.assignedTo,
        Status: faultData.status
      });
      
      setFaults(faults.map(f => 
        f.id === faultData.id ? response.data.fault : f
      ));
      return true;
    } catch (error) {
      console.error('Error updating fault:', error);
      setError('Failed to update fault');
      return false;
    }
  };

  // Event handlers and other functions
  const handleClickOutside = (e) => {
    if (notifRef.current && !notifRef.current.contains(e.target)) {
      setShowNotifications(false);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    onLogout();
    navigate("/login");
  };

  // Effects
  useEffect(() => {
    fetchFaults();
  }, []);

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    if (showNotifications && notifications.some((n) => !n.isRead)) {
      setNotifications(notifications.map((n) => ({ ...n, isRead: true })));
    }
  }, [showNotifications, notifications, setNotifications]);

  // Filtering logic
  const [searchTerm, setSearchTerm] = useState("");
  const [filterUrgency, setFilterUrgency] = useState("all");
  const [filterStatus, setFilterStatus] = useState("all");

  const filteredFaults = faults.filter((fault) => {
    const matchesSearch =
      (fault.DescFault && fault.DescFault.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (fault.Location && fault.Location.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (fault.ReportedBy && fault.ReportedBy.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesUrgency = filterUrgency === "all" || (fault.Urgency && fault.Urgency.toLowerCase() === filterUrgency);
    const matchesStatus = filterStatus === "all" || (fault.Status && fault.Status.toLowerCase() === filterStatus.toLowerCase());

    return matchesSearch && matchesUrgency && matchesStatus;
  });

  // Rest of your component JSX remains the same...
  return (
    <>
      {/* Your existing JSX */}
    </>
  );
}
