import React, { useState, useEffect } from "react";

const API_BASE_URL = "http://localhost:5000/api/auth";

export default function LoginPage({ onLogin }) {
  const [step, setStep] = useState("login"); // 'login' | 'register'
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);
  const [passwordValid, setPasswordValid] = useState(true);

  // Auto-clear messages after 5 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setError(null);
      setSuccess(null);
    }, 5000);
    return () => clearTimeout(timer);
  }, [error, success]);

  // Validate password strength in real-time for registration
  useEffect(() => {
    if (step === "register") {
      setPasswordValid(password.length >= 6 || password.length === 0);
    }
  }, [password, step]);

  const resetForm = () => {
    setUsername("");
    setEmail("");
    setPassword("");
    setRole("user");
    setError(null);
    setSuccess(null);
    setPasswordValid(true);
  };

  const validateForm = () => {
    if (!username.trim()) {
      setError("Username is required");
      return false;
    }
    if (!password.trim()) {
      setError("Password is required");
      return false;
    }
    if (step === "register") {
      if (!email.trim()) {
        setError("Email is required");
        return false;
      }
      if (!passwordValid) {
        setError("Password must be at least 6 characters long");
        return false;
      }
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError("Please enter a valid email address");
        return false;
      }
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.trim(),
          password,
        }),
      });

      const result = await response.json();
      
      if (!response.ok) {
        throw new Error(result.message || 'Login failed');
      }

      // Debugging log to verify response structure
      console.log('API Response:', result);

      // Ensure the response has the expected structure
      if (!result.user || typeof result.user.role === 'undefined') {
        throw new Error('Server response is missing required user data');
      }

      // Store token and user data
      localStorage.setItem('token', result.token);
      localStorage.setItem('user', JSON.stringify(result.user));
      
      setSuccess("Login successful!");
      
      if (onLogin) {
        onLogin({
          token: result.token,
          user: result.user
        });
      }
      
    } catch (err) {
      console.error('Login error:', err);
      
      if (err.message.includes('401') || err.message.includes('Invalid')) {
        setError("Invalid username or password");
      } else if (err.message.includes('Inactive')) {
        setError("Your account is inactive");
      } else {
        setError(err.message || "Login failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    
    if (!validateForm()) return;
    
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.trim(),
          email: email.trim().toLowerCase(),
          password,
          role,
        }),
      });

      const result = await response.json();
      
      if (!response.ok) {
        throw new Error(result.message || 'Registration failed');
      }
      
      setSuccess(result.message || "Registration successful! You can now log in.");
      resetForm();
      setStep("login");
      
    } catch (err) {
      console.error('Registration error:', err);
      
      if (err.message.includes('409') || err.message.includes('exists')) {
        setError("Username or email already exists");
      } else {
        setError(err.message || "Registration failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const switchStep = () => {
    resetForm();
    setStep(step === "login" ? "register" : "login");
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4" style={{ backgroundColor: "#0b1e39" }}>
      <div 
        className="w-full max-w-md p-8 rounded-2xl shadow-2xl"
        style={{ backgroundColor: "#12345b" }}
      >
        <h3 className="text-2xl font-bold text-white text-center mb-6">
          {step === "login" ? "Login" : "Register"}
        </h3>

        {error && (
          <div className="mb-4 p-3 bg-red-600 text-white rounded-lg text-sm relative">
            {error}
            <button 
              onClick={() => setError(null)}
              className="absolute top-2 right-2 text-white hover:text-gray-300 text-lg leading-none"
              aria-label="Close error message"
            >
              ×
            </button>
          </div>
        )}

        {success && (
          <div className="mb-4 p-3 bg-green-600 text-white rounded-lg text-sm relative">
            {success}
            <button 
              onClick={() => setSuccess(null)}
              className="absolute top-2 right-2 text-white hover:text-gray-300 text-lg leading-none"
              aria-label="Close success message"
            >
              ×
            </button>
          </div>
        )}

        <form onSubmit={step === "login" ? handleLogin : handleRegister}>
          <div className="space-y-4">
            <div>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                disabled={loading}
                className="w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
                style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
                aria-label="Username"
              />
            </div>

            {step === "register" && (
              <div>
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={loading}
                  className="w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
                  aria-label="Email"
                />
              </div>
            )}

            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={loading}
                minLength={step === "register" ? 6 : undefined}
                className={`w-full p-3 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 pr-12 ${
                  !passwordValid ? "border-red-500" : "focus:ring-blue-500"
                }`}
                style={{ backgroundColor: "#102c4a", borderColor: !passwordValid ? "#ef4444" : "#345b96" }}
                aria-label="Password"
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-3 text-gray-400 hover:text-white"
                aria-label={showPassword ? "Hide password" : "Show password"}
              >
                {showPassword ? "👁️" : "🙈"}
              </button>
              {step === "register" && (
                <p className={`text-xs mt-1 ${
                  passwordValid ? "text-gray-400" : "text-red-400"
                }`}>
                  Password must be at least 6 characters long
                </p>
              )}
            </div>

            {step === "register" && (
              <div>
                <select
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  disabled={loading}
                  className="w-full p-3 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  style={{ backgroundColor: "#102c4a", borderColor: "#345b96" }}
                  aria-label="User role"
                >
                  <option value="user">User</option>
                  <option value="admin">Admin</option>
                  <option value="technician">Technician</option>
                </select>
              </div>
            )}

            <button
              type="submit"
              disabled={loading || (step === "register" && !passwordValid)}
              className="w-full p-3 rounded-lg text-white font-bold text-lg hover:opacity-90 transition-opacity disabled:opacity-50 disabled:cursor-not-allowed"
              style={{ backgroundColor: "#345b96" }}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  {step === "login" ? "Logging in..." : "Registering..."}
                </span>
              ) : (
                step === "login" ? "Login" : "Register"
              )}
            </button>
          </div>
        </form>

        <button
          onClick={switchStep}
          disabled={loading}
          className="w-full mt-4 text-blue-300 hover:text-blue-200 transition-colors disabled:opacity-50 text-sm"
        >
          {step === "login" 
            ? "Don't have an account? Register" 
            : "Already have an account? Login"
          }
        </button>
      </div>
    </div>
  );
}
